function _upadateParam(_pname) {
   var _xvalue = env.get(_pname);
   if (_xvalue) {
      let temp = _xvalue.toString().trim();
      if (temp.startsWith('global.'))
         _xvalue = global.get(temp.substring(7));
      else if (temp.startsWith('flow.'))
         _xvalue = flow.get("$parent." + temp.substring(5));
      else if (temp.startsWith('msg.'))
         _xvalue = msg[temp.substring(4)];
      // extra eval           
      else if (temp.startsWith('@'))
         _xvalue = eval(temp.substring(1));
   }

   // optional: to set the parameter dynamically with a message.command property, same name
   //      if((msg.command)&&(msg.command[_pname]))
   //        _xvalue = msg.command[_pname];

   // save actual value in the (sub)flow context           
   flow.set(_pname, _xvalue);
}

// CUSTOMIZE HERE
// get actual values
_upadateParam("Max_time");
// done
return msg;

// looptimer-advanced output === input
// this filters controls messages
if (msg.payload)
      return msg;
return null;

let dpx = env.get("dp");
/*
====  decodes and explodes structure (recursive) for one dp of a device.
6: "CNQAAGQAACIAAA==" (in) to get atomic:
_6.V: 226
_6.Leack: 0
_6.A: 0.1
_6.W: 0.034
*/

function is_pure_object(val) {
    // from https://stackoverflow.com/questions/57227185/how-to-detect-if-a-variable-is-a-pure-javascript-object
    if (!val)
        return false;
    let proto = Object.getPrototypeOf(val);
    return proto == Object.prototype || proto == null
}
// recursive function
function explodeStep(name, data) {
    Object.entries(data).forEach(([key, value]) => {
        if (is_pure_object(value))
            explodeStep(name + "." + key, value);
        else
            msg.payload.data.dps[name + "." + key] = value;
    });
}

if ((msg.payload) && (msg.payload.data) && (msg.payload.data.dps) && (msg.payload.data.dps[dpx])) {
    const data = context.global.datadecode[env.get("decode")](msg.payload.data.dps[dpx]);
    explodeStep("_" + dpx, data);
}

return msg;

const DDIGITS = env.get("decimal digits");  // decimal digits on averages (truncate)
const DEVID = env.get('deviceID');

var inObj = null;
// ------------- devices logging msg process
if ((msg.payload) && (msg.payload.data)) {
  // get the device id:
    if (msg.payload.data.cid) {
      // is virtual    
      inObj = context.global.alldevices.getODev(msg.payload.data.cid, "virtual");
    } else {
      inObj = context.global.alldevices.getODev(msg.payload.deviceId);
    }
  // store ? 
  //  node.warn(["user "+inObj.id + " permission: ", global.get(inObj.id + "_AVERAGE")]);
  if ((inObj) && (global.get(inObj.id + "_AVERAGE") === undefined))
    node.error("WARNING: global." + inObj.id + "_AVERAGE variable not found!. No AVERAGE process.");

  if ((inObj) && (inObj.id != DEVID)) {
    node.error(["WARNING: bad ID on message. No AVERAGE process."], msg);
    return (msg);
    }

  if ((!inObj) || !(global.get(inObj.id + "_AVERAGE")))
    return (msg);

  // test: in array dp_exclude? if yes return msg
  let dpexclude = env.get('dp_exclude');
  if (Array.isArray(dpexclude) && (dpexclude.length >= 1)) {
    dpexclude.forEach(function (edp) {
      //    node.warn(["testing " + edp, msg]);
      if (msg.payload.data.dps[edp] !== undefined) {
        let newMsg = RED.util.cloneMessage(msg);
        newMsg.payload.data.dps = {};
        newMsg.payload.data.dps[edp] = msg.payload.data.dps[edp];
        node.send(newMsg);
        delete msg.payload.data.dps[edp];
        //            node.warn(["AVG exclude: ", newMsg, msg]);
      }
    });
  } // endif dpexclude
  // more dps? store
  if (Object.keys(msg.payload.data.dps).length > 0) {
    // node.warn(["AVG storing", msg]);
    let tmp = flow.get(inObj.id) || [];
    tmp.push(msg);
    flow.set(inObj.id, tmp);
  } // endif store
//  else
//     node.warn(["AVG kill", msg]);
    return null;
} // endif devicemsg


// ------------- trigger average msg process
if ( (msg.topic) && (msg.topic == 'average')) {
  const msgStore = flow.get(DEVID) || [];
  if (msgStore.length == 0) return null;
  // 
  let newMsg = RED.util.cloneMessage(msgStore[0]);
  delete newMsg.payload.data.dps;
  newMsg.payload.data.dps = {};
   // node.warn(["AVG out start", newMsg, msgStore]);
  let dsum = {};
  let dcount = {};
  // structures initialize      
  const devObj = context.global.alldevices.getODev(DEVID);
  devObj.dps.forEach(function (property) {
    const dpname = property.dp.toString();
    dsum[dpname] = null;
    dcount[dpname] = 0;
    if (env.get("dp_min").includes(dpname))
      dcount[dpname] = "min";
    if (env.get("dp_MAX").includes(dpname))
      dcount[dpname] = "max";

    //     node.warn(["in dps step ",devObj,dpname, dsum, dcount]);
  });
  //   node.warn(["init ",devObj,newMsg,dsum,dcount]);
  // data collect  
  msgStore.forEach(function (smsg) {
    Object.keys(smsg.payload.data.dps).forEach(function (key, index) {
      if ((smsg.payload.data.dps[key]) || (smsg.payload.data.dps[key] === 0)) {
        if (isNaN(smsg.payload.data.dps[key])) {
          dsum[key] = smsg.payload.data.dps[key];
          dcount[key] = -1;
        } else if ((dcount[key] == "min") && ((dsum[key] == null) || (dsum[key] > smsg.payload.data.dps[key]))) {
          dsum[key] = smsg.payload.data.dps[key];
        } else if ((dcount[key] == "max") && ((dsum[key] == null) || (dsum[key] < smsg.payload.data.dps[key]))) {
          dsum[key] = smsg.payload.data.dps[key];
        } else if (dcount[key] === 0) {
          dsum[key] = smsg.payload.data.dps[key];
          dcount[key] = 1;
        } else if ((!isNaN(dcount[key])) && (dcount[key] > 0)) {
          dsum[key] += smsg.payload.data.dps[key];
          dcount[key]++;
        }
      }
    });
  });
  // output process
  //  node.warn(["process ",newMsg,dsum,dcount]);
  Object.entries(dcount).forEach(function ([key, value]) {
   if ((value === -1) || (value === "min") || (value === "max")) {
      if (dsum[key] !== null)
        newMsg.payload.data.dps[key] = dsum[key];
    } else if ((!isNaN(value)) && (value > 0)) {
      if (env.get('dp_round').includes(key))
        newMsg.payload.data.dps[key] = Math.round(dsum[key] / value);
      else
        newMsg.payload.data.dps[key] = Number((dsum[key] / value).toFixed(DDIGITS));
    }
  });

  // node.warn(["AVG out done"+ newMsg.payload.deviceId ,newMsg,dsum,dcount]);
  flow.set(DEVID, []);
  return newMsg;
} // endif tiggermsg

return null;

