// to set global.tuyasyatus.xxxx._connected 
// creates a response for the dp "_connected"
// store value and filter events

var CONN = "_connected";  //  tuyadaemon internal define
// see also tuyaDAEMON."connection check" function
// local function
function _sendReal(idevice, value) {
   // builds a fake OUT message 
   var newMsg = {
      "payload": {
         "deviceId": idevice,
         "data": {
            "dps": {
               [CONN]: value
            }
         }
      }
   };
   return newMsg;
}

// user set parameter device id (simple)
var deviceId = env.get("deviceId");

var nowconnected;
if ((msg.payload !== undefined) && (msg.payload.state !== undefined)) {
   nowconnected = (msg.payload.state == "CONNECTED" || msg.payload.state == "node-red:common.status.connected");        // can change
}
if ((msg.status !== undefined) && (msg.status.text !== undefined)) {
   nowconnected = (msg.status.text == "CONNECTED" || msg.status.text == "node-red:common.status.connected");        // can change
}
if (nowconnected == undefined) return null;
// filering 
let old = context.get(deviceId + "_con");
// node.warn(["Static retry ",msg, nowconnected, old]);
// no changes, no message
if (old === nowconnected) return null;

context.set(deviceId + "_con", nowconnected);
// 16/05/2023 to get better control skip start unconnected message
if ((old === undefined) && (nowconnected == false)) return null;
if ((old == "none") && (nowconnected == false)) return null;
//
return (_sendReal(deviceId, nowconnected));

