func:
// to set global.tuyasyatus.xxxx._connected

const CONN = "_connected"; //  pseudoDPs
const TMT = "_findTimeout";
const RMT = "_retryTimeout";

// local functions
function _msgLogging(idevice, value) {
   // builds a fake OUT message
   var newMsg = {
      "payload": {
         "deviceId": idevice,
         "data": {
            "dps": {
               [CONN]: value
            }
         }
      }
   };
   return newMsg;
}

function _msgTimeoutCommand(idevice, timeout) {
   var tmtMsg = {
      "toDev": idevice,
      "payload": {
         "dps": TMT,
         "set": timeout
      }
   };
   return tmtMsg;
}

function getRandomArbitrary(min, max) {
   return Math.random() * (max - min) + min;
}

// ------------------  main
const XfLo = 1.0; // min increment 0%
const XfHi = 1.4; // min increment 40%
// user set parameter device id
let deviceId = env.get("deviceId");
let Tmin = flow.get("MinTimeout");
let TMAX = flow.get("MaxTimeout");
//
let cold = context.get(deviceId + "_con");
let cnew = cold;
let told = context.get(deviceId + "_time");
let tnew = told;
let cMsg = null;
let tMsg = null;
//status.text
if (msg.payload == undefined) return [null];
switch (msg.payload.state) {
   case "CONNECTED":
      tnew = Tmin;
      cnew = "connected";
      break;
   case "DISCONNECTED":
   case "ERROR":
      if (told < TMAX) {
         tnew = Math.floor(told * getRandomArbitrary(XfLo, XfHi));
      }
      cnew = "connecting";
      break;
   case "CONNECTING":
      cnew = "connecting";
      break;
}
//  out: 1. to device
//       2. to logging
// node.warn(["Dynamic retry ",msg, cnew, cold]);
if (cnew !== cold) {
   // 16/05/2023 to get better control skip start unconnected message
   if ((cold !== undefined) && (cold !== "none"))
       cMsg = _msgLogging(deviceId, cnew === "connected");
   context.set(deviceId + "_con", cnew);
}

if (tnew !== told) {
   tMsg = _msgTimeoutCommand(deviceId, tnew);
   context.set(deviceId + "_time", tnew);
}
return ([cMsg, tMsg]);

