function _upadateParam(_pname) {
   var _xvalue = env.get(_pname);
   if (_xvalue) {
      let temp = _xvalue.toString().trim();
      if (temp.startsWith('global.'))
         _xvalue = global.get(temp.substring(7));
      else if (temp.startsWith('flow.'))
         _xvalue = flow.get("$parent." + temp.substring(5));
      else if (temp.startsWith('msg.'))
         _xvalue = msg[temp.substring(4)];
      // extra eval           
      else if (temp.startsWith('@'))
         _xvalue = eval(temp.substring(1));
   }

   // optional: to set the parameter dynamically with a message property, same name
   //      if((msg.command)&&(msg.command[_pname]))
   //           _xvalue = msg.command[_pname];

   // save actual value in the (sub)flow context           
   flow.set(_pname, _xvalue);
}

// CUSTOMIZE HERE
// get actual values
_upadateParam("MinTimeout");
_upadateParam("MaxTimeout");
// done
return msg;

// GET: response from tuyastatus.

function _getGlobalValue(Dn, Pn){
var tuyastatus = global.get("tuyastatus");
if ((tuyastatus !== undefined) && (tuyastatus[Dn] !== undefined)) { // ok device field
   if (Pn === "schema") 
          return tuyastatus[Dn];
   return tuyastatus[Dn][Pn];
   }
return undefined;
}

// ===========================  main

let command = msg.payload.operation || 'SET';
// node.warn(["in command", command, msg]);
const PREFIX = "/ozone/timerpdm/1/";

if (command === 'GET'){
      // value from tuyastatus, format as device response
      let oD    = context.global.alldevices.getODev(msg.to);
      var dName = context.global.alldevices.getDevName(oD);
      var pName = context.global.alldevices.getDpsName(oD, msg.infodp);
      var value = _getGlobalValue(dName, pName);
      if (value !== undefined){
            var newMsg = { 
               "payload":{
                  "deviceId": msg.to,
                  "deviceName": dName,
                  "data": {
                      "dps":{
                          [msg.payload.dps]: value  
                  }}}};
          return newMsg;
          }
return null;          
}

// based on info msg every 1s
// rules:
// - always when 'led' change.
// - default (tstart == 0): only when 'led' change.
// else every refreshCicle secons, until flow.Max > 0 
let tcount = flow.get("tcount");
let cycle = flow.get("tstart");
let max = flow.get("Max");
if (cycle > 0){
  tcount = ( ++tcount) % cycle;
  flow.set("tcount",tcount);
  }
let skip = ((cycle === 0) || (tcount !== 0)) ;
//  timeout
if (max <= 0)
    skip = true;
else 
    flow.set("Max",--max);
//  
let next = flow.get("next");
if (next){
    skip = false;
    flow.set("next", false);
    }
// node.warn({"tcount":tcount,"cycle":cycle,"max":max,"next": next});
//
var newMsg = { 
    "payload":{
        "deviceId": "_sonoff001",
        "data": {
             "dps":{
            }}}};
//
let odata = JSON.parse(msg.payload);
if (odata[1] === flow.get("lastled")) {
   if (skip) return null;
}
flow.set("lastled", odata[1]);
newMsg.payload.data.dps["_info"] = odata[0];
switch(odata[1]){
  case 0:
     newMsg.payload.data.dps["_led"] = "gray";
     break;
  case 1:
     newMsg.payload.data.dps["_led"] = "green";
     break;
  case 2:
     newMsg.payload.data.dps["_led"] = "red";
     break;
}
return newMsg;

var newMsg = { 
    "payload":{
        "deviceId": "_sonoff001",
        "data": {
             "dps":{
            }}}};
let odata = JSON.parse(msg.payload);
newMsg.payload.data.dps["_mode"] = odata["mode"] ==0?"OFF":odata["mode"] ==1?"AUTO":"LOOP";
newMsg.payload.data.dps["_ONtime"] = odata["ONtime"];
newMsg.payload.data.dps["_OFFtime"] = odata["OFFtime"];
newMsg.payload.data.dps["_PDM"] = odata["PDM"];
newMsg.payload.data.dps["_timezone"] = odata["timezone"];
// change
newMsg.payload.data.dps["_startTime"] = odata["Ttime"];
return newMsg;

var newMsg = { 
    "payload":{
        "deviceId": "_sonoff001",
        "data": {
             "dps":{
            }}}};
let odata = JSON.parse(msg.payload);
newMsg.payload.data.dps["_mode"] = odata["mode"] ==0?"OFF":odata["mode"] ==1?"AUTO":"LOOP";
newMsg.payload.data.dps["_PDM"] = odata["PDM"];
newMsg.payload.data.dps["_count"] = odata["count"];
// name change
newMsg.payload.data.dps["_clock"] = odata["Time"];
newMsg.payload.data.dps["_switch"] = odata["output"];  //  converted by BOOLEANONOFF
newMsg.payload.data.dps["_countdown"] = odata["toggle"];
return newMsg;

// SET: format change, and some name/values changes

function isNone(value){   // same as in 'REFRESH'
    if(value){
        if(typeof(value) === "string"){
            if(value.toUpperCase() === 'OFF') return true;
            if(value.toUpperCase() === 'NO') return true;
            if(value.toUpperCase() === 'NONE') return true;
        }
    return false;
    }
return true;
}

let command = msg.payload.operation || 'SET';
const PREFIX = "/ozone/timerpdm/1/";

if (command == 'SET'){
       switch(msg.payload.dps) {
           case '_go':    // MQTT commands
           case '_off':
           case '_end':
           case '_send':
               msg.topic=PREFIX + "command";
               msg.payload = msg.payload.dps.substring(1);
               flow.set("next", true);
               return msg;
           case '_refresh': // name change
 //              msg.payload ="send";
 //              msg.topic=PREFIX + "command";
 //              node.warn("in refresh");
               flow.set("next", true);
