        node.warn("ERROR: SET commands not allowed by the '" + msg.payload.device + "'");
        return [null, null, null, null];
    }

    req.payload.dps = msg.infodp.toString(); // dps always string: "101" or "_connected"
    req.payload.set = msg.payload.value;
}  //  --------------------  SET ends

//
switch (capp) {
    //  "RW,WW,GW,RO,WO,TRG,PUSH,SKIP,UNK,NULL",
    case "UNK":
    case "RW":  // ok, nothing to do
        break;
    case "PUSH":
        node.warn("ERROR: Can not SET/GET the 'PUSH' DP '" + msg.payload.device + '"."' + msg.payload.property + "'");
        return [null, null, null, null];
    case "RO":
        if (_isset(msg)) {
            node.warn("ERROR: Can not SET the Read Only DP '" + msg.payload.device + '"."' + msg.payload.property + "'");
            return [null, null, null, null];
        }
        break;
    case "WO":
        if (_isget(msg)) {
            node.warn("ERROR: Can not GET the 'WO' DP '" + msg.payload.device + '"."' + msg.payload.property + "'");
            return [null, null, null, null];
        }
        if (msg.payload.value === null) {
            node.warn("ERROR: Can not use 'SET:null' for 'WO' DP '" + msg.payload.device + '"."' + msg.payload.property + "'");
            return [null, null, null, null];
        }
        break;
    case "GW":
        if (_isset(msg)) {
            node.warn("ERROR: Can not SET the 'GW' DP '" + msg.payload.device + '"."' + msg.payload.property + "'");
            return [null, null, null, null];
        }
        // transforms GET in SET:null
        delete req.payload.operation;
        req.payload.set = null;
        break;
    case "WW":
        if (_isget(msg)) {
            // transforms GET in SET:null
            delete req.payload.operation;
            req.payload.set = null;
        }
        break;
    case "TRG":
        node.warn("ERROR: '" + msg.payload.device + '"."' + msg.payload.property + "' has 'TRG' capability");
        return [null, null, null, null];
    case "SKIP":
        return [null, null, null, null];
    default:
        node.warn("ERROR: unknown capability for '" + msg.payload.device + '"."' + msg.payload.property);
        return [null, null, null, null];
}

// outputs: tuya/fake/info/db/logging
// builds the DB  log
query.topic = " INSERT INTO `messages` (`timestamp`, `daemon`, `action`, `device-id`, `device-name`, `dps`, `dp-name`, `data`, `value`) ";
query.topic += " VALUES ('" + _mySQLdatetime() + "','" + myId + "', 'TX', '" + _getID(o) + "', '" + formatName(msg.payload.device) + "','" + req.payload.dps + "', '" + formatName(msg.payload.property) + "', " + (_isset(msg) ? "'" + formatData(req.payload.set) + "'" : "NULL") + ", " + (_isset(msg) ? "'" + formatData(uVal) + "'" : "NULL") + " );";

// the warning message
if (_isset(msg))
    warn = {
        payload: ["TX SET: device " + msg.payload.device + "/" + msg.payload.property, msg.payload.value],
        to: msg.to,
        infodp: msg.infodp,
        hide: msg.hide
    };
else
    warn = {
        payload: "TX GET: device " + msg.payload.device + "/" + msg.payload.property,
        to: msg.to,
        infodp: msg.infodp,
        hide: msg.hide
    };

return ([req, null, warn, query]);

