}

// ---------------- main
// catch bad
if (msg.status === undefined) return null;
//
if (msg.status.text === undefined) return (null);
//
var olddb = flow.get(PDB);
var newdb = ((msg.status.text == "connected") || (msg.status.text == "OK"));
if (olddb == newdb) return null;
//
flow.set(PDB, newdb);
//
if (newdb) {
    node.status({
