//  for applications, fast processing, immediate answer.... 
//  Return JSON; data or status.
//  GET, SCHEMA: solved from tuyastaus, returns data (not stored in log)
//  SET, MULTIPLE: sended to tuyaDEAMON, returns 'status:sended' (stored in log)
//-------------------------------- local functions:

var comm = {};
var tuyastatus = global.get("tuyastatus");
var myId = global.get("instance_name");

// node.warn(["REST msg ",msg]) ;

if (msg.payload && !(msg.payload.remote_from))
    msg.payload.remote_from = "REST." + myId;
else    
    msg.payload.remote_from = myId;

delete msg.payload.remote;


// some messages are resolved here:
// ----------- is LIST
if (!msg.payload.device) {     // devices list immediate
    if (tuyastatus !== undefined)
        msg.payload["list"] = Object.keys(tuyastatus);
    //             msg.payload["remote_from"] = myId; 
    let jdat2 = JSON.stringify(msg.payload);
    msg.payload = jdat2;
    return ([msg, null]);
}

// -----------------------  struff: msg normalization
var o = context.global.alldevices.getODev(msg.payload.device);
var p = context.global.alldevices.getODps(o, msg.payload.property);
msg = context.global.alldevices.normalize(msg, o, p);
if (msg.error) {
    node.warn(["[REST] " + msg.error, msg]);
    return ([msg, null]);
}
if (msg.warning) {
    node.warn(["[REST] " + msg.warning, msg, o, p]);
}

// node.warn(["msg normalized ",msg]) ;

comm.to = msg.to;
comm.infodp = msg.infodp;
comm['payload'] = msg.payload;
// msg.payload.value  string or JSON or null
try {
    comm.payload.value = JSON.parse(msg.payload.value);
} catch (e) {
    comm.payload.value = (msg.payload.value) ? msg.payload.value : undefined;
}
// ----------- is device SCHEMA
if (msg.infodp === "schema") { // ok device field
    //----- is SCHEMA
    msg.payload['schema'] = tuyastatus[msg.payload.device];
    delete (msg.payload.schema._t);
 //   msg.payload["remote_from"] = myId;
    // stringify         
    let jdat2 = JSON.stringify(msg.payload);
    msg.payload = jdat2;
    return ([msg, null]);
}

//  node.warn(["Trace general remote",msg]);

// general case:  
// -------- is GET, read data from tuyastatus
if (msg.payload.value === undefined) {
    //  msg.payload["remote_from"] = myId; 
    if ((tuyastatus[msg.payload.device]) && (tuyastatus[msg.payload.device][msg.payload.property] !== undefined)) {
        let connected = tuyastatus[msg.payload.device]["_connected"];
        if (connected)
            msg.payload['value'] = tuyastatus[msg.payload.device][msg.payload.property];
        else
            msg.payload['value'] = "disconnected";
        let jdat2 = JSON.stringify(msg.payload);
        msg.payload = jdat2;
        return ([msg, null]);
    } else {
        msg.payload['value'] = "none";
        let jdat2 = JSON.stringify(msg.payload);
        msg.payload = jdat2;
        return ([msg, null]);
    }
} // end GET

//--------- is SET, MULTIPLE, process it
let pseudoDP = context.global.PSEUDODP.includes(msg.infodp.toUpperCase());
if (((p !== undefined) || pseudoDP) && (msg.payload.value !== undefined)) {
    node.send([null, comm]);  // to do cmd
    delete msg.payload.value;
    msg.payload['status'] = "sent";
    let jdat2 = JSON.stringify(msg.payload);
    msg.payload = jdat2;

    // todo: check infinite loop case  
    //   return [msg, null, comm];  // to rest, to notify
    return [msg, null, null];  // to rest, to notify
} // end SET

return [msg, null];

//  for debug, all request sended to devices,
//  asyncronous, wait device answer or timout (set here and in external trigger node)
//  Return JSON, data or status.
var timeout = 4; // in sec, less than 'REST timeout'  node
var restautoma = flow.get('restautoma');
 // test only
 //  node.warn(["Asycronous request", msg]);
switch (restautoma) {
   case '_wait':
      if ((msg.req !== undefined) && (msg.payload !== undefined)) { // HTTP message
         if (msg.payload.device === undefined) {     // back devices list immediate
            // immediate answer
            msg.payload = [];
            const tuyastatus = global.get("tuyastatus");
            if (tuyastatus !== undefined)
               msg.payload = Object.keys(tuyastatus);
            return ([null, msg]);
         } else {            // send request, set timeout
            var comm = {};
            comm['payload'] = msg.payload;
            flow.set('restautoma', msg);
            flow.set('restt', Math.floor(Date.now() / 1000));
            // msg.payload.value  string or JSON or null
            try {
               comm.payload.value = JSON.parse(msg.payload.value);
            } catch (e) {
               comm.payload.value = (msg.payload.value) ? msg.payload.value : undefined;
            }
            return ([comm, null]);
         }
      }

      break;
   default:
      //  node.warn("in default");
      // uses payload.info in info messages
      if (msg.info !== undefined) {
         if ((typeof restautoma === 'object') && (restautoma.req !== undefined) && (restautoma.payload.device !== undefined)) {
            //        node.warn(["from REST automa default",restautoma, msg]);
            if (restautoma.payload.device === msg.info.device) {
               if ((restautoma.payload.property === undefined) && (restautoma.payload.value === undefined)) {  // is schema, ok
                  // schema processing from tuyasatus
                  let tuystatus = global.get("tuyastatus");
                  let xschema = tuystatus[restautoma.payload.device];
                  delete (xschema._t);
                  restautoma.payload = JSON.stringify(xschema);
                  flow.set('restautoma', '_wait');  // done
                  return ([null, restautoma]);
               }
               if ((restautoma.payload.property == msg.info.property) || (restautoma.payload.property == msg.infodp) || (restautoma.payload.property === 'MULTIPLE')) {
                  var jdat = JSON.stringify(msg.info);
                  restautoma.payload = jdat;
                  flow.set('restautoma', '_wait');
                  return ([null, restautoma]);
               }
            }
         }
      }

}

// test timeout, if not wait

if (restautoma !== '_wait') {
   //  node.warn("in timeout");
   var start = flow.get('restt');
   if (((Math.floor(Date.now() / 1000)) - start) > timeout) { // OK exit for timeout
      flow.set('restautoma', '_wait');
      if ((typeof restautoma === 'object') && (restautoma.req !== undefined)) {
         restautoma.payload = { "status": "request aborted for timeout" };
         return ([null, restautoma]);
      }
   }
}

//noting to do
return ([null, null]);

