// Test msg.
// if 'hide' contains 'K' destroy (not on tuyastatus +)

function getDeviceHide(id) {
  var oDev = context.global.alldevices.getODev(id);
  if (!(oDev)) return "";
  var mask = (oDev.hide || "");
  var oDP = context.global.alldevices.getODps(oDev, msg.payload.property);
  if (!(oDP)) return (mask);
  mask += (oDP.hide || "");
  return (mask);
}

//  detects some fatal errors in input message structure, here for convenience
if (!(msg.payload.deviceId)) {
  node.warn(["ASSERT fail: in the answer the payload.deviceId must be defined.", msg]);
  return null;
}

if (!(msg.payload.data)) {
  node.warn(["ASSERT fail: in the answer the payload.data is mandatory", msg]);
  return null;
}
// Add the 'hide' string an Kills devices
msg.hide = getDeviceHide(msg.payload.data.cid || msg.payload.deviceId,);
if (!!~msg.hide.indexOf("K")) return null;

return msg;

// note: warning on 'eval()' are ok.

if (msg.share === undefined) {
    //   node.warn(["ERROR in 'share' object for '" + (msg.info?(msg.info.device + "/" + msg.info.property):"standalone share") + "'", msg]);
    return (null);
}


function recursiveExecute(obj) {
    for (const property in obj) {
        //       node.warn(["testing: "+property,obj[property]]);
        if ((typeof (obj[property]) === 'string') && (obj[property].startsWith('@'))) {
            //           node.warn(["processing: ", obj[property]]);
            obj[property] = eval(obj[property].substring(1));
        } else if (typeof (obj[property]) === 'object') {
            recursiveExecute(obj[property]);
        }
    }
}

function evalExecute(aVal) {
    // accepts: strings, JSON, object
    // output: string or object after eval leaf like '@....'
    if ((typeof (aVal) === 'string') && (aVal.startsWith('{') || aVal.startsWith('['))) {
        const data = JSON.parse(aVal);
        recursiveExecute(data);
        return data;
    }
    if ((typeof (aVal) === 'string') && aVal.startsWith('@')) {
        return eval(aVal.substring(1));
    }
    if (typeof (aVal) === 'object') {
        recursiveExecute(aVal);
        return (aVal);
    }
    return (aVal);
}

//    node.warn(["SHARE input", msg])   ;

if (Array.isArray(msg.share)) {
    var arrayLength = msg.share.length;
    var tuyastatus = global.get("tuyastatus");
    for (var i = 0; i < arrayLength; i++) {
        let oktodo = true;
        if (Array.isArray(msg.share[i]["test"])) {
            try {
                oktodo = msg.share[i]["test"].reduce((ok, cond) => ok && eval(cond), true);
            } catch (error) {
                node.warn(["Error processing 'share[" + i + "].test' for '" + (msg.info ? (msg.info.device + "/" + msg.info.property) : "standalone share") + "': " + error, msg.share[i]]);
            }
        }

        if (Array.isArray(msg.share[i]["action"])) {
            if (oktodo) {
                var actionLength = msg.share[i]["action"].length;
                for (var j = 0; j < actionLength; j++) {
                    let xshare = msg.share[i]["action"][j];
                    // default from caller values or undefined.  
                    var _cmd = {
                        payload: {
                            device: msg.info ? msg.info.device : undefined,
                            property: msg.info ? msg.info.property : undefined,
                            value: msg.info ? msg.info.value : undefined
