context.global.datadecode.STRUCTRAND = function (value) {
    // switch random data struct
    function minTohm(min) { // local function
        const h = (~~(min / 60));
        const m = min % 60;
        return (h > 9 ? h : '0' + h) + ":" + (m > 9 ? m : '0' + m);
    }
    function daystring(val) {
        // from 1 byte return sting like 'DL--G--' (it)
        // week starting on sunday (domenica)
        var res = "";
        const week = "DLMMGVS"; // en: SMTWTFS
        let mask = 1;
        for (var i = 0; i < 7; i++) {
            res += (val & mask) ? week.charAt(i) : '-';
            mask = mask << 1;
        }
        return (res);
    }
    if (value.length < 5)
        return (value);
    const b = Buffer.from(value, "base64");
    var randdef = [];
    for (var i = 0; i < b.length / 6; i++) {
        var o = {
            active: (b.readUInt8(i * 6 + 0) !== 0),
            days: daystring(b.readUInt8(i * 6 + 1)),
            start: minTohm(b.readUInt16BE(i * 6 + 2)),
            end: minTohm(b.readUInt16BE(i * 6 + 4))
        };
        randdef[randdef.length] = o;
    }
    return randdef;
};

