context.global.alldevices.normalize = function (xmsg, Do, Po) {
    if (!Do) {
        xmsg['error'] = "ERROR [normalize]: not found the device '" + xmsg.payload.device + "' in global.alldevices.";
        return (xmsg);
    }
    xmsg.payload.device = (Do.name) ? Do.name : (Do.cid || Do.id);
    xmsg['to'] = Do.cid || Do.id;
    if (!this.fake.includes(Do))
        xmsg['toDev'] = Do.cid ? Do.gateway : Do.id;
    xmsg['infodp'] = 'schema';
    if (xmsg.payload.property) {
        if ((Po === undefined) || (Po.dp === undefined)) {
            xmsg.infodp = xmsg.payload.property;
            if (!context.global.PSEUDODP.includes(xmsg.payload.property.toUpperCase()))
                xmsg['warning'] = "WARNING [normalize]: In alldevices missed the '" + xmsg.payload.device + "':dps[" + xmsg.payload.property + "] definition.";
        } else {
            xmsg.infodp = Po.dp.toString();
            xmsg.payload.property = Po.name || Po.dp;
        }
    }
    if (xmsg['infodp'] === 'schema')
        delete (xmsg.payload.value);
    xmsg.hide = ((Do && Do.hide) ? Do.hide : "") + ((Po && Po.hide) ? Po.hide : "");
    return (xmsg);
}

// given a value and a DP-object
// returns the value encoded or the good type (uses: typefield | type | default rules) )
context.global.alldevices.encodeValue = function (value, dpObj = {}) {
    if ((value === undefined) || (dpObj === undefined)) return (value);
    // general values 'null' 
    if ((value === '') || (value === null) || (value === 'NULL') || (value === 'null')) return (null);
    if ((!dpObj.typefield) || (dpObj.typefield === 'NONE') || (dpObj.typefield === 'NULL')) {
        try {
            switch (dpObj.type) {      // type forces output
                case 'boolean':
                    return (!((value === false) || (value === 'false') || (value === 'FALSE') || (value === 0)));
                case 'int':
                case 'enum':
                    return (Number(value));
                case 'string':
                    return (value.toString());
                default:              // also undefined: default rules
                    if (typeof (value) === 'boolean') return (value);
                    if (value === 'false') return (false);
                    if (value === 'true') return (true);
                    return ((parseInt(value) == value) ? parseInt(value) : value);
                // "37" => 37, but "4.5" => "4.5"
            }
        }
        catch (error) {
            return ({ error: 'ERROR [encodeValue]: In conversion from value(' + value + ') to type ' + dpObj.type + ': ' + error });
        }
    } else {    // try the encode function
        if (!context.global.dataencode[dpObj.typefield])
            return ({ error: 'ERROR [encodeValue]: Not found the encode.' + dpObj.typefield + ' function().' });
        let nval;
        try {
            nval = (context.global.dataencode[dpObj.typefield](value));
        }
        catch (error) {
            return ({ error: 'ERROR [encodeValue]: In encode.' + dpObj.typefield + ' function, on value (' + value + '): ' + error });
        }
        return (nval);
    }
}
