// Implementation of GET/SET/SCHEMA  commands 
// for the 'fake' device "_system"
// part 1: use of _getGlobalValue(mx), fast commands

// ------------- check for ALL fake cmds, here for convenience
if (typeof(msg.to) === 'undefined')
         node.warn(["ASSERT fail: in Fake-msg the msg.to (id) must exist.",msg]) ;   

if (msg.toDev)
         node.warn(["ASSERT fail: in Fake-msg the msg.toDev is NOT required.",msg]) ;   

if (typeof(msg.infodp) !== "string")
         node.warn(["ASSERT fail: msg.infodp (string) must exist.",msg]) ;   

if (msg.payload.dp)
         node.warn(["temporary ASSERT fail: msg.payload.dp (string) is obsolete.",msg]) ;   

// from 2.2.0
if(typeof(msg.hide) !== "string")
         node.warn(["ASSERT fail: in msg the msg.hide (string) must exist." , msg]) ;   

// 
const DEVIDSYS = "_system";
// fast exit: not SYSTEM: handled be other module
if (msg.to !== DEVIDSYS) return([null]);

// --------------- generic stuff
// test: idDev GET command?
function _isGetCommand(msx) {
    return ((msx.infodp !== undefined) && (msx.payload.value === undefined));
}

// test: idDev SET command?
function _isSetCommand(msx) {
    return ((msx.infodp !== undefined) && (msx.payload.value !== undefined));
}

function _getGlobalValue(mx){
var tuyastatus = global.get("tuyastatus");
if (tuyastatus[mx.payload.device] !== undefined) { // ok device field
   if (mx.infodp === "schema") 
          return tuyastatus[mx.payload.device];
   return tuyastatus[mx.payload.device][mx.payload.property];
   }
return undefined;
}

function _getHome() {
    return (global.get("instance_name"));
}

// base message to log
var newMsg = {
        "payload": {
            "deviceId": "_system",
            "data": {
                "dps": {}
            }
        }
    };

if (_isGetCommand(msg)) {  // a GET, but some only 
   switch (msg.infodp){
// implemented reading tuyastatus       
      case "_ACpower":
      case "_WiFinet":
      case "_LANnet":
      case "_laststart":
      case "_timerON":
      case "_timerOFF":
      case "_ACunconnected":
      case "_WiFiunconnected":
      case "_doSCHEMA":
      case "_doUPDATE":
      case "_doBenchmark":
//      case "_benchmark_step":  to 2
//      case "_benchmark_end" :  to 2
      case "_proxy":
//      case "_name":
      case "_doTrigger":
      case "_exec":
            //all GETs implemented reading tuyastatus
            newMsg.payload.data.dps[msg.infodp] = _getGlobalValue(msg);
            return([null, newMsg]);
/* deplaced in CORE since v. 2.2.0
case "_name":  
          // special: name is defined in CORE global,  must read global.instance_name
            newMsg.payload.data.dps[msg.infodp] = global.get('instance_name');
//            node.warn(["trace _name", msg, newMsg]);
            return([null, newMsg]);
*/            
      case "_doBenchmark":
             return([null]);
      case "schema":
            // implemented from tuyastatus.
            // in schema case, _getGlobalValue   returns  all the dps: {dp:value,...}
            // filter off pseudoDP in tuyastatus (_connected, _t...)
             Object.entries(_getGlobalValue(msg)).forEach(([key, value]) =>{
                if ( ! context.global.PSEUDODP.includes(key.toUpperCase())) {
                     newMsg.payload.data.dps[key]=value;
                    }
            });
            return([null, newMsg]);
    }
}// endif GET

//
if (_isSetCommand(msg)) {  // a SET, precess here only _proxy
    switch (msg.infodp){
      case "_proxy":
          // set  the http REST request
            let urls = global.get("remotemap");
            var out = {
                   url: null,
                   headers: null,
                   payload : msg.payload.value, 
                   };
 // puts in payload.remote_from itself (e.g. DEVPC)                   
               out.payload["remote_from"] =  _getHome();     
               let tor  = msg.payload.value.remote;
               if (tor ===  _getHome()){
                    out.url = urls.local[tor]+"/tuyaDAEMON";
                } else {
                    out.url = urls.remote[tor]+"/tuyaDAEMON";
                }
                if(out.url === "undefined/tuyaDAEMON"){
                   node.warn(["ERROR [_system]: server "+tor+" not found: ", urls]);
                   newMsg.payload.data.dps[msg.infodp] = "ERROR: server not found";
                   return ([null, newMsg]);
            }
// only value JSON            
            let jv = JSON.stringify(out.payload.value);
            out.payload.value = jv;
            delete out.payload.remote;
            newMsg.payload.data.dps[msg.infodp] = "sent";
            return([out,newMsg]);
   }
} // endif SET
// not found: no error.  goto next selectors 2, 3
return([null, null, msg]);

