// input  checks tuya_bridge messages
// output 1: the msg.tuyatrigger is the value from tuya-bridge, for more processing.
// output 2: the low-level SET 0 (ACK) for tuya-bridge.
var xdp = global.get('COUNTDP');

var xtime = 0;
// it is a counter message?
if (msg.payload.data && (msg.payload.data.dps[xdp] != undefined))
    xtime = msg.payload.data.dps[xdp];
else
    return [];

if (xtime < 60) return [null];   // 0 or bad value, nothing to do    
// special case for 'GUARD', to allow tests (pings)
// ACH not required, done allway by tuya automation
if (xtime == global.get('LOWGUARD')) return [null];
if (xtime == global.get('MIRRORGUARD')) return [null];

// set in msg the dedicated value tuyatrigger
msg.payload.tuyatrigger = xtime;
msg.payload.data = {};

const isMaster = flow.get('is_master');

// it is a TRIGGER?
if (xtime % flow.get('COUNTSTEP') == 0) {
// uses triggerMAP values to select TUYATRG and to send ACK (if master)   
    if ((xtime >= 10010) && (xtime < 20020)){
        return [null, msg, isMaster ? msg : null];
    };
    if ((xtime >= 30030) && (xtime < 50050)){
        return [msg, null, isMaster ? msg : null];
    }
 // else nothing to do   
 return [null, null, null];
}

// not a TRIGGER, it is a PUSH: does only the ACK fallback)
return [null, null, isMaster ? msg : null];

var delta;
var xdp = global.get('COUNTDP');
// in 'On Start' sets flow._tsstart to 0 (init)
// it is a counter message?  is ACK ?
if (!(msg.payload.data && (msg.payload.data.dps[xdp] == 0)))
        return [];

// output:  finish/more loops

// is test on ?
var old = flow.get("_tsstart");
if (old === 0) return [];  // not in ping test

// close this ping    
var back = Date.now();
delta = back - old;
// fallback in case of timeout: keep alive
if ((delta > 5000) || (delta < 20)) {
        flow.set("_tsstart", 0);
        return ([null, msg]);
}

// accept, updates stats
var stats = flow.get("_pingstat");
//flow.set("pingstat",{
//     test: 0,
//     total:0,
//     min: 100000000000,
//     max:0
// set data    
stats.sum += delta;
if (stats.min > delta)
        stats.min = delta;
if (stats.max < delta)
        stats.max = delta;
stats.test++;
stats.total--;
flow.set("_pingstat", stats);
flow.set("_tsstart", 0);

if (stats.total > 0) {       // more  tests  to do
        return [null, msg];
}

// done, does statistics & response msg  
var st = {
        "deviceId": global.get('TUYABRDG'),
        "data": {
                "dps": {
                        "_tstPing": {
                                "count": stats.test,
                                "avg": Math.floor(stats.sum / stats.test),
                                "max": stats.max,
                                "min": stats.min
                        }
                }
        }
};
msg.payload = st;
// sends results  
return ([msg, null]);

// Select on dp and send to tuya-smart-device or handle new pseudo-DP commands.

