  // here future expansions
  default:
    // standard switch processing
    return [msg, null];
}

// Run as soon as possible after all 'On Start' and 'Global Config'.
// note: the run order of all 'Global config' and all 'On Start' is undefined.
// So, if some 'On Start' function  requires 'Global Config' data, palace it there.
// --------------------------------------------------- standard
const asDEVICE = global.get("TUYABRDG");     // singleton in alldevices
const asMODULE = "modules.core_trigger";
const VERSION = "2.2.2";

function init_tuyastatus(odev, conn0) {
    var pmDevName = context.global.alldevices.getDevName(odev);
    // ---------------- inizializes global tuyastatus
    let tstatus = "tuyastatus['" + pmDevName + "']";
    // init using only _connected (not in alldevices), so does cleanup
    global.set(tstatus, { _connected: conn0 });
    // init all GETable dps with default value
    odev.dps.forEach(adp => {
        if (['RW', 'WW', 'GW', 'RO', 'UNK', 'PUSH', undefined].includes(adp.capability)) {
            // this access style required to handle spaces in usr-names
            let dpname = "['" + (adp.name ? adp.name : adp.dp) + "']";
            switch (adp.dp) {     // some special init:   
                default:
                    global.set(tstatus + dpname, "none");
            }
        }
    });
};

// --------------------------------------------------- standard
if (!global.get("coresetup")) {
    node.warn("TRIGGER STARTUP ERROR: adjust this startup delay.");
    return ([null]);
}
// --------------------- init startup for the flow.
global.set(asMODULE, VERSION);  // update modules
// access stuff
// --- see also different implementation in 'core.core flow setup' node.
const oDev = context.global.alldevices.getODev(asDEVICE,"real");
if (oDev === undefined) {
    node.warn("[startup] ERROR: not found the device '" + asDEVICE + "' in global.alldevices.");
    return;
}
init_tuyastatus(oDev, false);
// --------------------------------------------- standard processing ends
//
// more flow init required now (and not in 'On Start') by some properties    
// int all 'mirror' devices
context.global.alldevices.fake.forEach(device => {
    if ((!device.id.startsWith("_")) && (device.id.length == 20)) {  // test mirror
        init_tuyastatus(device, true);
    };
});

return msg;

function yesSpecial(str) {
    if (!str) return str;
    let tmp = str.replace('%23', '#').replace('%2B', '+').trim();
    return ((tmp === undefined) || (tmp === '')) ? undefined : tmp.replace('%25', '%');     // the only spaces (' ') becomes undefined
}

let inptop = msg.topic + '//////';
let pars = inptop.split("/");
// 11/05/2023: updated using ir, id, ip
let xtopic = flow.get("topic-commands");
let xparts = xtopic.split("/");
let ir, id, ip = -1;
for (let idx = 0; idx < xparts.length; idx++) {
    if (xparts[idx] === '+remote+') ir = idx;
    if (xparts[idx] === '+device+') id = idx;
    if (xparts[idx] === '+property+') ip = idx;
}
let newmsg = { payload: {} };
// node.warn(["pre in MQTT", pars, xparts]);
// if (pars[0] == xparts[0]) {
newmsg.payload['remote'] = ir >= 0 ? pars[ir] : null;
newmsg.payload['device'] = yesSpecial(id >= 0 ? pars[id] : null);
newmsg.payload['property'] = yesSpecial(ip >= 0 ? pars[ip] : null);
newmsg.payload['value'] = (msg.payload) ? JSON.parse(msg.payload) : msg.payload;
// node.warn(["post in MQTT", ir, id, ip, newmsg]);
// null cases
if (!newmsg.payload.value) delete (newmsg.payload.value);
if (!newmsg.payload.property) delete (newmsg.payload.property);
if (!newmsg.payload.device) delete (newmsg.payload.device);
return newmsg;
//}
// return null;

//
function noSpecial(str) {
    if (str === undefined) return ("");
    let tmp = str.replace('%', '%25').replace('#', '%23');
    return tmp.replace('+', '%2B');
}

let remote = global.get("instance_name");
if (msg.remote_from && (msg.remote_from !== "NULL"))
    remote = msg.remote_from;
let xtopic = flow.get("topic-events").replace("+remote+", remote);

// Special case list devices 
if ((msg.from === "_system") && (msg.infodp === "list") && (msg.info.value.length > 0)) {
    msg.info.value.forEach((device, index) => {
        const lmsg = {
            topic: xtopic.replace("+device+", noSpecial(device)).replace("/+property+", ""),
            //            topic: 'tuyaDAEMON/' + remote + '/' + noSpecial(device) + '/event',
            payload: null
        };
        node.send(lmsg);
    });
    return null;
}

// Special case list devices   ( from _system/_tuyastatus)
if ((msg.from === "_system") && (msg.infodp === "_tuyastatus") && (msg.info.value.list !== undefined)) {
    msg.info.value.list.forEach((device, index) => {
        const lmsg = {
            topic: xtopic.replace("+device+", noSpecial(device)).replace("/+property+", ""),
            //        topic: 'tuyaDAEMON/' + remote + '/' + noSpecial(device) + '/events',
            payload: null
        };
        node.send(lmsg);
    });
    return null;
}

// Special case schema devices   ( from _system/_tuyastatus)
if ((msg.from === "_system") && (msg.infodp === "_tuyastatus") && (msg.info.value.value.schema !== undefined)) {
    //         msg.info.value.value.schema.forEach((dps, val) => {
    for (const [dps, val] of Object.entries(msg.info.value.value.schema)) {
        const lmsg = {
            //           topic: 'tuyaDAEMON/' + remote + '/' + noSpecial(msg.info.value.device) + '/events/' + noSpecial(dps),
            topic: xtopic.replace("+device+", noSpecial(msg.info.value.device)).replace("+property+", noSpecial(dps)),
            payload: (val === null || val === "") ? '"null"' : JSON.stringify(val)
        };
        if (dps !== '_t')
            node.send(lmsg);
    }
    return null;
}
// general case
// let xtopic = 'tuyaDAEMON/' + remote + '/' + noSpecial(msg.info.device) + '/events/' + noSpecial(msg.info.property);
const newmsg = {
    topic: xtopic.replace("+device+", noSpecial(msg.info.device)).replace("+property+", noSpecial(msg.info.property)),
    payload: (msg.info.value === "" || msg.info.value === "") ? '"null"' : JSON.stringify(msg.info.value)
}
if (msg.info.property === '_t')
    return null;
return newmsg;

// So, if some 'On Start' function  requires 'Global Config' data, palace it there.
// --------------- locals
const asMODULE = "modules.core_mqtt";
const VERSION = "2.2.2";
// --------------------------------------------------- standard
if (!global.get("coresetup")) {
    node.warn("MQTT STARTUP ERROR: adjust the startup delay.");
    return ([null]);
}
// --------------------- init startup for the flow.
global.set(asMODULE, VERSION);  // update

// Implementation of GET/SET/SCHEMA/MULTIPLE  commands
// for any 'fake' device
//    1) the msg.to IS the device ID
//    2) the msg.infodp IS the dp ID
//    3) the msg.payload.device IS the device name (if it exists, or ID) used by tuyastatus
//    4) the msg.payload.property IS the dp name (if it exists, or dP) used by tuyastatus
// smoke detector accepts only SET, absorb all msg.
// --------------- locals
// mapping msg to outputs
const DEVID = "here_smoke_id";

// test: DEVID GET command?
function _isGetCommand(msx) {
    return ((msx.to === DEVID) && (msx.infodp !== undefined) && (msx.payload.value === undefined));
}

// test: DEVID SET command?
function _isSetCommand(msx) {
    return ((msx.to === DEVID) && (msx.infodp !== undefined) && (msx.payload.value !== undefined));
}


// ------------- main
// select and execute all commands for 'smoke detector'
// accepts only SET. final: absorb all bad msg + error.

if (_isSetCommand(msg)) { // test: is SET for DEVID?
    switch (msg.infodp) {
        case "30310":   // same as in global.alldevices, unique
            // -----------  select by value - tolerant, 'false' accepts 'OFF','NO', 0, "0", "", [],[0],[[]], null, undefined :  see https://stackoverflow.com/questions/359494/which-equals-operator-vs-should-be-used-in-javascript-comparisons
            if (context.global.dataencode.BOOLEANONOFF(msg.payload.value))
                return ([msg]); // true: output#1
            return ([null, msg]); //false: output#2
    }
}

// default: warning, bad command
if (msg.to === DEVID) {
    node.warn(["Unknown command for the smoke detector.", msg]);
}
