// converts the msg (dp/set) to serial format required by PM2.5USB
function formatI(n) { // local function
    return (n > 99 ? "" + n : (n > 9 ? "0" + n : "00" + n));
}

// returns now(), formatted for mySQL, never fails
function _mySQLdatetime() {
    //datetime format for mySQL: '2020-12-18 20:45:02'
    // using 'sv' for date format (but local timezone)
    return (new Date().toLocaleString('sv'));
}

// test: idDev SET command?
function _isSetCommand(idDev, msx) {
    return ((msx.to === idDev) && (msx.payload.property !== undefined) && (msx.payload.value !== undefined));
}

// test: idDev GET command?
function _isGetCommand(idDev, msx) {
    return ((msx.to === idDev) && (msx.payload.property !== undefined) && (msx.payload.value === undefined));
}

const DEVID = "_PM1_USB";
// from property/value to serial for PM detector
if (_isSetCommand(DEVID, msg)) { // test: is SET
    var data = "";
    switch (msg.payload.dp) {
    case "1":
    case 1:
        flow.set("pm_" + msg.payload.dp, msg.payload.value);
        data = {
            fun: "01",
            sendtime: formatI(msg.payload.value)
        }
        break;
    case "2":
    case 2:
        flow.set("pm_" + msg.payload.dp, msg.payload.value);
        data = {
            fun: "02",
            storetime: formatI(msg.payload.value)
        }
        break;
    case "3":
    case 3:
        if (( msg.payload.value === undefined) || ( msg.payload.value == false))
            msg.payload["value"] = _mySQLdatetime().substring(2);
        flow.set("pm_" + msg.payload.dp, msg.payload.value );
        data = {
            fun: "03",
            clock: msg.payload.value
        }
        break;
    case "4":
    case 4:
        data = {
            fun: "04"
        }
        break;
    case "5":
    case 5:
        flow.set("pm_" + msg.payload.dp, (msg.payload.value ? "1" : "0"));
        data = {
            fun: "05",
            flag: (msg.payload.value ? "1" : "0")
        }
        break;
    case "6":
    case 6:
        data = {
            fun: "06"
        }
        break;
    default:
        node.warn("The SET command '" + msg.payload.property + "' => '" + msg.payload.value + "' is unknown for PM detector device '");
        return;
    }
    msg.payload = JSON.stringify(data);
    return msg;
}

if (_isGetCommand(DEVID, msg)) { // test: is GET
    // only one
    if (msg.payload.dp == 80) {
        msg.payload = '{"fun":"80"}';
        return msg;
    } else {
        node.warn("The GET command '" + msg.payload.property  + "' => '" +  msg.payload.value + "' is unknown for PM detector device '");
        return;
    }
}

