// convert serial data from PM2.5 to usual format
// payload: object
//     deviceId: "123455aa196ae5******"
//     data: object
//        t: 1609234903
//        dps: object
//           102: 1020
//
var answ = {
    deviceId: "_PM1_USB",
    data : {
          t : Math.floor(Date.now() / 1000),
          dps : {

          }
       }
    }

var data = JSON.parse(msg.payload.replace(",}","}")); 

switch (data.res) {
        case "1":
        case "2":
        case "3":
              answ.data.dps[data.res] = flow.get("pm_"+data.res);
              break;
        case "5":
              answ.data.dps[data.res] =  " done ";
              break;
        case "4":
              if (flow.get("skipdata")) return;
              if (Number(data["cpm1.0"]) == 0) return;  // bad data
              answ.data.dps = {
                 200: Number(data["cpm1.0"]),
                 201: Number(data["cpm2.5"]),
                 202: Number(data["cpm10"]),
                 203: Number(data["apm1.0"]),
                 204: Number(data["apm2.5"]),
                 205: Number(data["apm10"]),
                 206: Number(data["aqi"]),
                 207: data["t"],
                 208: Number(data["r"])
              }
              break;
        case "6":
              answ.data.dps[data.res] = "done ";
              break;
        case "10":       // start historical data
              flow.set("skipdata", true);
              return;
        case "80":
              answ.data.dps = {
                 1 : Number(data["SendInteralTime"]),
                 2 : Number(data["StoreInteralTime"]),
                 80: Number(data["WritePoint"]),
                 81: Number(data["ReadPoint"]),
                 82: Number(data["SendInteralFlag"])
              }
              break;

   }
msg.payload = answ;    
return msg;

if(flow.get("skipdata") === false) return;

// out: cmd/histo
var data = JSON.parse(msg.payload);

var answ = {
    deviceId: "_PM1_USB",
    data: {
        t: Math.floor(Date.now() / 1000),
        dps: {}
    }
}

var cmd = {
    payload: {
        "fun":"05", 
        "flag":"1"
    }
}

switch (data.res) {

case "0":   // end historical data
    sum = flow.get("sum");
    count = flow.get("count");
    for (i = 0; i < 24; i++) {
        if (count[i] > 0) {
            sum["cpm1.0"][i] = Math.round(sum["cpm1.0"][i] / count[i]);
            sum["cpm2.5"][i] = Math.round(sum["cpm2.5"][i] / count[i]);
            sum["cpm10"][i]  = Math.round(sum["cpm10"][i] / count[i]);
            sum["apm1.0"][i] = Math.round(sum["apm1.0"][i] / count[i]);
            sum["apm2.5"][i] = Math.round(sum["apm2.5"][i] / count[i]);
            sum["apm10"][i]  = Math.round(sum["apm10"][i] / count[i]);
            sum["aqi"][i]    = Math.round(sum["aqi"][i] / count[i]);
            sum["t"][i]      = (sum["t"][i] / count[i]).toFixed(1);
            sum["r"][i]      = (sum["r"][i] / count[i]).toFixed(1);
        }
    }
    answ.data.dps[300] = "["+sum["cpm1.0"].toString() + "]";
    answ.data.dps[301] = "["+sum["cpm2.5"].toString() + "]";
    answ.data.dps[302] = "["+sum["cpm10"].toString() + "]";
    answ.data.dps[303] = "["+sum["apm1.0"].toString() + "]";
    answ.data.dps[304] = "["+sum["apm2.5"].toString() + "]";
    answ.data.dps[305] = "["+sum["apm10"].toString() + "]";
    answ.data.dps[306] = "["+sum["aqi"].toString() + "]";
    answ.data.dps[307] = "["+sum["t"].toString() + "]";
    answ.data.dps[308] = "["+sum["r"].toString() + "]";
    msg.payload = answ;
    flow.set("skipdata", false);
    return [cmd, msg];

case "4":
    sum = flow.get("sum");
    count = flow.get("count");
    count[Number(data["h"])]++;
    sum["cpm1.0"][Number(data["h"])] += Number(data["cpm1.0"]);
    sum["cpm2.5"][Number(data["h"])] += Number(data["cpm2.5"]);
    sum["cpm10"][Number(data["h"])]  += Number(data["cpm10"]);
    sum["apm1.0"][Number(data["h"])] += Number(data["apm1.0"]);
    sum["apm2.5"][Number(data["h"])] += Number(data["apm2.5"]);
    sum["apm10"][Number(data["h"])]  += Number(data["apm10"]);
    sum["aqi"][Number(data["h"])]    += Number(data["aqi"]);
    sum["t"][Number(data["h"])]      += Number(data["t"]);
    sum["r"][Number(data["h"])]      += Number(data["r"]);

    flow.set("sum", sum);
    flow.set("count", count);
    return;

case "10":
    return;
}
return;
