// Implementation of GET/SET/SCHEMA/MULTIPLE  commands
// for any 'fake' device
//    1) the msg.to IS the device ID
//    2) the msg.infodp IS the dp ID
//    3) the msg.payload.device IS the device name (if exist, or ID) used by tuyastatus
//    4) the msg.payload.property IS the dp name (if exist) used by tuyastatus
// smoke detector accepts only SET, absorb all msg.
// --------------- locals
// mapping msg to outputs
const DEVID = "317080408caab521a5d3";


// test: idDev GET command?
function _isGetCommand( msx) {
    return ((msx.to === DEVID) && (msx.infodp !== undefined) && (msx.payload.value === undefined));
}

// test: idDev SET command?
function _isSetCommand(msx) {
    return ((msx.to === DEVID) && (msx.infodp !== undefined) && (msx.payload.value !== undefined));
}

//  set (and create, if required) global.tuyastatus structure from msg
function _setGlobalValue(msx) {
    var tuyastatus = global.get("tuyastatus");
    if (tuyastatus == undefined)
        tuyastatus = {};
    if (tuyastatus[msx.payload.device] == undefined)
        tuyastatus[msx.payload.device] = {};
    tuyastatus[msx.payload.device][msx.payload.property] = msx.payload.value;
    global.set("tuyastatus", tuyastatus);
}

