//input: device OUT msg (real)
//  _msgid: "1234f3fd.8477fc"
//  _event: "node:1233963.a7d146"
//  payload: object
//     deviceId: "123455aa196ae5******"
//     deviceName: "*tuya_bridge" (tuya name optional, not used)
//     data: object
//        t: 1609234903
//        dps: object
//           102: 1020
//
//input: device OUT msg (virtual)
//   _msgid: "d0b26a44.e81738"
//   payload: object
//      deviceId: "123473b1b786b59*******"
//      deviceName: "Zigbee Gateway"
//      data: object
//        cid: "12358d00086a1c45"
//        t: 1609235292
//        dps: object
//           103: 19
//
// output0: info message:
//            warmsg.payload: ["RX: '" + <device-name> + "'/'" + <property-name> + "' ", <data-expandible>] // for debug pad
//            warmsg.info={                                 // for internal use, REST, etc.
//                         device: <device-name>, 
//                         property: <property-name>,    
//                         dp : <dp>,    
//                         value: <processed-data>,    
//                         };
//
// output1: msg, input message + msg.topic: the sql for DB
//
// -------------------------------------------------- do not change


// local functions:
function _callJSONMethod(obj, fname, a, b, c, d) {
    // to use the stored JSON method library in global alldevices 
    // see: https://stackoverflow.com/questions/49125059/how-to-pass-parameters-to-an-eval-based-function-injavascript
    var wrap = s => "{ return " + obj[fname] + " };" //return the block having function expression
    var func = new Function(wrap(obj[fname]));       // ignore the ⚠ node-red warning
    return func.call(null).call(obj, a, b, c, d);    //invoke the function using arguments
}

// -------------------------------------------- private functions
var alld = global.get("alldevices");
var oDev = null;
var _dataname = "";


// updates the global "tuyastatus" and warmsg
function _addToStatus(dnx, _datanx, dx, tx) {
    var tuyastatus = global.get("tuyastatus");
    if (tuyastatus === undefined)
        tuyastatus = {};
    if (tuyastatus[dnx] === undefined)
        tuyastatus[dnx] = {};
    tuyastatus[dnx][_datanx] = dx;
    tuyastatus[dnx]._t = tx;
    global.set("tuyastatus", tuyastatus);
    // info message
    warmsg = {
        payload: ["RX: '" + dnx + "'/'" + _datanx + "' ", dx]
    };
}

// clean data (object, array) using JSON.stringify, to get a string for DB
function formatData(data) {
    var clean = JSON.stringify(data);
    if (clean[0] === '"')
        clean = clean.substring(1).slice(0, -1);
//    return clean.replace(/'/g, '&#39;');  // sigle quotes are dangerous in sql
//    return clean.replace(/'/g, '\\x27');
    return clean.replace(/'/g, "\\'");  
    }      

// For DB output, string
function formatName(data) {
    if(data == null) return 'NULL';
//    return data.replace(/'/g, '&#39;');  // sigle quotes are dangerous in sql
//    return data.replace(/'/g, '\\x27');
      return data.replace(/'/g, "\\'");  
    }      

// true if device is a real device (virtual := cid exists)
function _isreal(mx) {
    return (mx.payload.data.cid === undefined);
}

// returns deviceId (real) or cid (virtual), never fails
function _getID(mx) {
    if (_isreal(mx))
        return (mx.payload.deviceId);
    return (mx.payload.data.cid);
}

// returns data.t or now(), formatted for mySQL, never fails
function _mySQLdatetime(mx) {
    //datetime format for mySQL: '2020-12-18 20:45:02'
    // using 'sv' for date format and local timezone
    if (mx.payload.data.t === undefined)
        return (new Date().toLocaleString('sv'));
    return (new Date(Number(mx.payload.data.t) * 1000).toLocaleString('sv'));
}

// returns val, processed by correct dpprocess[] function, if required, and sets  _dataname
function _processData(odev, mx, adpn, val) {
    _dataname = "";

    var oPx = _callJSONMethod(alld, "__getODps", odev, adpn);

    if (oPx === undefined) {
    // tuyadaeamon internal defined fields for all devices - standard process optional, no warning
        if  (adpn !== "_connected")   // extra internal defined dps
             node.warn("In alldevices missed the '" + oDev.name + "':dps[" + adpn + "] definition.");
        _dataname = adpn;
        return (val);
    }
    _dataname = (oPx.name === undefined) ? adpn : oPx.name;

    if ((oPx.typefield === undefined)||(oPx.typefield === 'NONE'))
        return (val); // no process
    if (datadecode[oPx.typefield] === undefined) {
        node.warn("Not found " + oPx.typefield + " decode function() for '" + _dataname + "': data unchanged.");
        return (val);
        }
    try {
        return datadecode[oPx.typefield](val);
        } catch (error) {
                node.warn("Error in " + oPx.typefield + " decode function on value (" + val + "): " + error);
                return (val);
                }

}

// get the device object from alldevices, if it don't exist return undefined + warn
function _getODevice(all, mx) {
    var o;
    if (_isreal(mx)) {
        o = _callJSONMethod(all, "__getODev", msg.payload.deviceId, "real");
        if (o === undefined) {
            o = _callJSONMethod(all, "__getODev", msg.payload.deviceId, "fake");
        }
        if (o === undefined) {
            node.warn("ERROR: ADD to alldevices the real/fake deviceId: " + mx.payload.deviceId + " (" + mx.payload.deviceName + ")");
        }
    } else {
        o = _callJSONMethod(all, "__getODev", mx.payload.data.cid, "virtual");
        if (o === undefined) {
            node.warn("ERROR: ADD to alldevices the virtual cid: " + mx.payload.data.cid + " [gateway: " + mx.payload.deviceId + "]");
        }
    }
    return (o);
}

//  detects some fatal errors in message structure
function _badMsg(mx) {
    if ((mx.payload.deviceId === undefined) && (mx.payload.data.cid === undefined)) {
        node.warn("ERROR: OUT message without payload.deviceID or payload.data.cid: mandatory");
        return true;
    }
    if (mx.payload.data === undefined) {
        node.warn("ERROR: OUT message without payload.data  (deviceID:" + mx.payload.deviceId + ")");
        return true;
    }
    if (typeof mx.payload.data === 'string')
        return false;
    if (mx.payload.data.dps === undefined) {
        node.warn("ERROR: OUT message without payload.data.dps (deviceID:" + mx.payload.deviceId + ")");
        return true;
    }
    return false;
}

// ---------------------  main
// sends any message to DB (in msg.topic), updates tuyastatus
var warmsg = null; // info debug (1)
msg.topic = ""; // sql query (2)

if (_badMsg(msg))
    return ([null, null]); //error sended, abort

oDev = _getODevice(alld, msg);
if (oDev === undefined)
    return ([null, null]); //error sended,  abort

var dname = _callJSONMethod(alld, "__getDevName", oDev);
// now processes dp=value couples
if (msg.payload.data.dps !== undefined) {
    Object.entries(msg.payload.data.dps).forEach(([key, value]) => {
        warmsg = null;
        if ((value !== undefined) && (value !== null)) {
            var pdata = _processData(oDev, msg, key, value);
            msg.topic += " INSERT INTO `messages` (`timestamp`, `device-id`, `device-name`, `dps`, `dp-name`, `data`, `value`)\n";
            msg.topic += " VALUES ('" + _mySQLdatetime(msg) + "', '" + _getID(msg) + "', '" + formatName(dname) +
      			"','" + key + "', '" + formatName(_dataname) + "', '" + formatData(value) + "', '" + formatData(pdata) + "') ;\n";
            if (_dataname !== undefined) {
                // timestamp as seconds
                var tx = msg.payload.data.dps.t;
                if (tx === undefined)
                    tx = Math.floor(Date.now() / 1000);
                _addToStatus(dname, _dataname, pdata, tx);
                // send data in info too
                warmsg['info']={};
                warmsg['info']['device'] =dname ;
                warmsg['info']['property'] =_dataname ;
                warmsg['infodp'] = key ;
                warmsg['info']['value'] =pdata ;
                node.send([warmsg, null]);  // SQL are added
            }
        }
    });
} else {
    if (msg.payload.data == "json obj data unvalid") {
        node.warn("ERROR from '" + dname + "': not JSON data but 'json obj data unvalid': operation not allowed?.");
    } else {
        // maybe here it is possible to deduce the value send: see
        var buff = Buffer.from(msg.payload.data);
        node.warn("ERROR from '" + dname + "': not JSON data but HEX:" + buff.toString('hex') + "  ('" + msg.payload.data + "')");
    }
}

if (msg.topic !== "") // some processed, SQL exists
    return ([null, msg]);

// kills unwanted DB queries
// -----------------------------------------
// use:
// msg.payload.deviceId  (device|gateway)
// msg.payload.data.cid  (for virtual devices)
// msg.payload.data.dps[dp]   single dp

// kills malformated messages
if((msg.payload.data === undefined) || (msg.payload.data.dps === undefined))  return (null);
//
// filter example: 
// kills all TRIGGERS messages (see tuyaTRIGGER)
 if(msg.payload.deviceId  == "bfa355aa196ae597****") { // from tuya_bridge (real)
    if (msg.payload.data.dps["102"] != undefined)    // select trigger (dp == 102)
         return(null);
    }

// if(msg.payload.deviceId === "_system"  ) return null;  
//  more....
return msg;

