// Data codification is function of the device and dps: here you can add new encode functions().
// To add a new data format:
// 1)  Add a new encoding function() here in the array dataencode, using a new 'TYPE_NAME'
// 2)  Add the dual decode function()) to "OUT data process" node.
// 3)  Add the new 'TYPE_NAME' in the DB tuyathone.lookupdecode table  (if you use tuyaDAEMON toolkit)
// 4)  now you can use the new 'TYPE_NAME' as typefield  in global.alldevices
// note tuya always BE
// version 1.1 (use of _callJSONMethod)
var dataencode = {

   BYTESMALLFLOAT: function (value) {
      // transforms temperature to byte: 19.5° => 195 => C3H
      // required by thermostat
      var x = value;
      if (typeof value === 'string')
         x = Number.parseFloat(value);
      if (Number.isNaN(x)) {
         throw "'value' is not a number!";
      }
      return ~~(x * 10.0);
   },

   BOOLEANONOFF: function (value) {
      // transforms 'ON'/'OFF' to boolean  true/false (very tolerant :  see https://stackoverflow.com/questions/359494/which-equals-operator-vs-should-be-used-in-javascript-comparisons)
      // it|en: can be localized for internationalization
      if ((value == 0) || (value == null) || (value.toUpperCase() === "OFF") || (value.toUpperCase() === "FALSE") || (value.toUpperCase() === "NO"))
         return false;
      return true;
   },

   BOOLEANOPENCLOSE: function (value) {
      // transforms 'OPEN'/'CLOSE' to boolean  true/false
      // it|en: can be localized for internationalization
      if ((value == 0) || (value == null) || (value.toUpperCase() === "CLOSE") || (value.toUpperCase() === "CHIUSA"))
         return false;
      return true;
   },

   ENUMONOFFHOLD: function (value) {
      // transforms 'OFF'/'ON'/'HOLD' to string '0'/'1'/'2'
      // it|en: can be localized for internationalization
      if ((value == 0) || (value == null) || (value.toUpperCase() === "OFF") || (value.toUpperCase() === "SPENTO") || (value.toUpperCase() === "NO"))
         return "0";
      if ((value == 1) || (value.toUpperCase() === "ON") || (value.toUpperCase() === "ACCESO") || (value.toUpperCase() === "YES"))
         return "1";
    return "2";
   },

   STRUCTARGETTEMP: function (value) {
      // transform the JSON structure:
      //   {"count":4,
      //    "changes":[{"time":"00:00","temp":15.5},
      //              {"time":"07:00","temp":20},
      //              {"time":"16:30","temp":21},
      //              {"time":"21:00","temp":16.5}]}
      // to  base64(Uint8Array[17])
      // required by thermostat

      function hmTomin(hm) { // local function
         pieces = hm.split(':');
         return (parseInt(pieces[0]) * 60 + parseInt(pieces[1]));
      }
      function mult10(value) {
         return ~~(Number.parseFloat(value) * 10.0)
      }

      var o = value;
      if (typeof value === 'string')
         o = JSON.parse(value);
      node.warn("in code " + value);
      if (o.count !== undefined) {
         var buf = Buffer.alloc(o.count * 4 + 1);

         buf.writeInt8(o.count, 0);
         for (var i = 0; i < o.count; i++) {
            buf.writeInt16BE(hmTomin(o.changes[i].time), i * 4 + 1);
            buf.writeInt16BE(mult10(o.changes[i].temp), i * 4 + 3);
         }
         return buf.toString('base64');
      } else {
         throw "'value' is not a {time,T}[4] struct";
      }
   },

   ARRAY8INT: function (value) {
      // Transforms a UInt8Array, e.g.[17,16,16,16,15,15,17,18,19,19,18,18,18,18,17,16,17,18,18,0,0,0,0,0]
      // as object or JSON string
      // to  coded64 data => ERAQEA8PERITExISEhIREBESEgAAAAAA
      // required by thermostat
      var o = value;
      if (typeof value === 'string')
         o = JSON.parse(value);
      if (Array.isArray(o)) {
         b = Buffer.from(o);
         return b.toString('base64');
      } else {
         throw "'value' is not a (json) array !";
      }
   },

   STRUCTTIMEHMS: function (value) {
      //  Transforms a time "HH:MM:SS"
      //  in coded data base64(Uint16Array[3])
      //  tentative, on work
      pieces = value.split(':');
      var buf = Buffer.alloc(6);
      buf.writeInt16LE(parseInt(pieces[0]), 0);
      buf.writeInt16LE(parseInt(pieces[2]), 2);
      buf.writeInt16LE(parseInt(pieces[4]), 4);
      return buf.toString('base64');
   },

   STRUCTCOLOUR: function (value) {
      // accepts: JSON or object
      // encode:
      //      {   "hex":"RRGGBBHHHHSSVV" }
      //  or: {   "r": RR,
      //          "g": GG,
      //          "b": BB
      //        ( "h":HHHH,
      //          "s": SS,
      //          "v": VV )  }
      // 100% = 0x64
      //  required by humidifier led
      var o = value;
      if (typeof value === 'string')
         o = JSON.parse(value);
      if ((o.hex !== undefined) && (typeof o.hex === 'string') && (o.hex.length == 14))
         return (o.hex);
      if ((o.r === undefined) || (o.g === undefined) || (o.b === undefined)) {
         throw "'value' is not valid hex|rgb object!";
      }
      var buf = Buffer.alloc(7);
      buf.writeUInt8(o.r, 0);
      buf.writeUInt8(o.g, 1);
      buf.writeUInt8(o.b, 2);
      if (o.h !== undefined) {
         buf.writeUInt16BE(o.h, 3);
         buf.writeUInt8(o.s, 5);
         buf.writeUInt8(o.v, 6);
      } else {
         buf.writeUInt16BE(0, 3);
         buf.writeUInt8(255, 5); //fixed ?
         buf.writeUInt8(255, 6); //fixed ?
      }
      return buf.toString('hex');
   },
   ENUMHIGHGOODLOW: function(value){
     // transforms 'LOW'/'GOOD'/'HIGH' to string '0'/'1'/'2'
      // it|en: can be localized for internationalization
      // required by switch
      if ((value == 0) || (value == null) || (value.toUpperCase() === "LOW") || (value.toUpperCase() === "BASSA"))
         return "0";
      if ((value.toUpperCase() === "HIGH") || (value == 2)|| (value.toUpperCase() === "ALTA"))
         return "2";
      return "1";
    },
    RECMODE: function(value){
     // transforms 'continuous'/'events' to string '1'/'2'
      // it|en: can be localized for internationalization
      // required by switch
      if ((value == 1)|| (value.toUpperCase() === "EVENTS")|| (value.toUpperCase() === "EVENTI"))
         return "1";
      return "2";
    },
    STRUCTINCH:  function(value){
      var o = value;
      if (typeof value === 'string')
         o = JSON.parse(value);
