      }
       function dayscode(val) {
          result = 0;
          mask  = 1;
          for (var i=0;i<7;i++){
              result |= (val.charAt(i) == '-')? 0:mask;
              mask = mask << 1;             
              }
          return(result);
       }

      var o = value;
      if (typeof value === 'string')
         o = JSON.parse(value);
      var buf = Buffer.alloc(10*o.length);
      for(let i= 0; i < o.length; i++){
          buf.writeUInt8((o[i].active)?1:0,i*10+0);
