// find object dp
    var oDev = _callJSONMethod(alld, "__getODev", "_system", "fake");
    var oPx  = _callJSONMethod(alld, "__getODps", oDev,  msg.payload.property);
// outputs: /AC/WiFi/Lan/
    switch (oPx.dp){
      case "_ACpower":
      case "_ACunconnected":
            return([msg,null, null]);
      case "_WiFinet":
      case "_WiFiunconnected":
             return([null,msg, null]);
      case "_LANnet":
            return([null,null, msg]);
   }
// not found: no error.  More _system commands  can exist, in other flows
}
return([null,null, null]);

var delta;

// test on all tuya_bridge messages
// output:  finish/ reset/more loops
if  ((msg.payload.tuyatrigger > 699) &&
     (msg.payload.tuyatrigger < 1000)){
     // fallback in case of missed  ACK: restart and keep alive
     flow.set("tsstart", 0);
     return([null, msg, msg]); 
     }

if(msg.payload.tuyatrigger != 0 ) // not ACK,  nothing to do
     return([null,null,null]); 

// standard 0 ACK,  nothing to do
    var old = flow.get("tsstart");
    if (old === 0)  return([null,null,null]);   // not in ping test

    var back = Date.now();
    delta = back - old;
    if ((delta > 2000) || (delta < 20)) { 
// fallback in case of timeout: restart and keep alive
         flow.set("tsstart", 0);
         return([null, null, msg]);
         }

// ok, updates stats
  var stats = flow.get("pingstat") ; 
 //flow.set("pingstat",{
 //     test: 0,
 //     total:0,
 //     min: 100000000000,
 //     max:0
 // set data    
   stats.sum += delta;
    if( stats.min > delta)
         stats.min = delta;
    if( stats.max < delta)
         stats.max = delta;
    stats.test++;  
    stats.total--;   
    flow.set("pingstat", stats);

  if (stats.total >0)  {       // more  tests  
      flow.set("tsstart", 0);
      return [null, null, msg];
      }   

// done, does statistics & msg  
 var st = {
    "deviceId":"_system",
    "data":{
       "dps":{
           "_trgPing":{
               "count": stats.test,
               "avg": Math.floor(stats.sum/stats.test),
               "max": stats.max,
               "min": stats.min
           }
       }
    }};  
   msg.payload = st;
   flow.set("tsstart", 0);
// sends results  
   return([msg, null, null]);

 flow.set("tsstart", 0);
 flow.set("pingstat",{
      total:node.name,
      test: 0,
      sum:0,
      min: 100000000000,
      max:0
});

return msg;

