//  SET, MULTIPLE: sended to tuyaDEAMON, returns 'status:sended' (stored in log)
//-------------------------------- local functions:
function _callJSONMethod(obj, fname, a, b, c, d) {
   // see: https://stackoverflow.com/questions/49125059/how-to-pass-parameters-to-an-eval-based-function-injavascript
   var wrap = s => "{ return " + obj[fname] + " };" //return the block having function expression
      var func = new Function(wrap(obj[fname])); // ignore the node-red ⚠ warning
   return func.call(null).call(obj, a, b, c, d); //invoke the function using arguments
}



var comm = {};
var tuyastatus = global.get("tuyastatus");
if (msg.payload.device === undefined){     // back devices list immediate
             msg.payload = [];
             if (tuyastatus !== undefined) 
                 msg.payload = Object.keys(tuyastatus);
             return ([msg, null]);
       }
if ((tuyastatus !== undefined) && (msg.payload.device !== undefined) && (tuyastatus[msg.payload.device] !== undefined)) { // ok device field

   if ((msg.payload.property === undefined) &&(msg.payload.value === undefined)) {  //----- is SCHEMA
         var jdat = JSON.stringify(tuyastatus[msg.payload.device]);
         msg.payload = jdat ;
         return ([msg, null]);
      }

   if ((msg.payload.property !== undefined) && (msg.payload.property.toUpperCase() === 'MULTIPLE') 
            && (msg.payload.value !== undefined)) {
         comm['payload'] = msg.payload;
         node.send([null, comm]);
         msg.payload = {"status": "sended"};
         return [msg, null];
      }
     if ((msg.payload.property !== undefined) &&
        (tuyastatus[msg.payload.device][msg.payload.property] === undefined)){  //------ maybe is ID?
         var alld = global.get("alldevices");
         var o = _callJSONMethod(alld, "__getODev", msg.payload.device);
         var p = _callJSONMethod(alld, "__getODps", o, msg.payload.property );
         if ((p !== undefined) &&  (p.name !== undefined))
                msg.payload.property  = p.name ;  // converted to name
      }

   if ((msg.payload.property !== undefined) && (msg.payload.value === undefined)) { // -------- is GET, read data from tuyastatus
         msg.payload['value'] = tuyastatus[msg.payload.device][msg.payload.property];
         var jdat2 = JSON.stringify(msg.payload);
         msg.payload = jdat2;
         return ([msg, null]);
         } 
   if ((msg.payload.property !== undefined) && (msg.payload.value !== undefined)) { 
                                                      //--------------------------------------- is SET, process it
         comm['payload'] = msg.payload;
         node.send([null, comm]);
         msg.payload = {"status": "sended"};
         return [msg, null];
         }
    //--------------------------- the property does not exist ??

// ======================  if here, only PROPERTY error
node.warn("REST ERROR: not found the property ("+msg.payload.property + ") in '"+ msg.payload.device+"'");
msg.payload = { "status": "ERROR: not found the property ("+msg.payload.property + ") in '"+ msg.payload.device+"'" };
return [msg, null];
} // ok device ends
// ======================  if here, only DEVICE ERROR
node.warn("REST ERROR: not found the device '"+ msg.payload.device+"'");
msg.payload = { "status": "ERROR: not found the device '"+msg.payload.device+"'" };
return [msg, null];

//  for debug, all request sended to devices,
//  asyncronous, wait device answer or timout (set here and in external trigger node)
//  Return JSON, data or status.
var timeout = 4; // in sec, less than 'REST timeout'  node
var restautoma = flow.get('restautoma');
switch (restautoma) {
case '_wait':
   if ((msg.req !== undefined) && (msg.payload !== undefined))  { // HTTP message
      if (msg.payload.device === undefined){     // back devices list immediate
              // immediate answer
             msg.payload = [];
             var tuyastatus = global.get("tuyastatus");
             if (tuyastatus !== undefined) 
                 msg.payload = Object.keys(tuyastatus);
            return ([null, msg]);
       } else {            // send request, set timeout
        var comm = {};
        comm['payload'] = msg.payload;
        flow.set('restautoma', msg);
        flow.set('restt', Math.floor(Date.now() / 1000));
        return ([comm, null]);
      }
   }

   break;
default:
   // uses payload.info in info messages
   if ((msg.info !== undefined) && (msg.info.device !== undefined)) {
       if ((typeof restautoma === 'object') && (restautoma.req !== undefined) && (restautoma.payload.device !== undefined)){
//          node.warn(["from automa",restautoma, msg]);
         if (restautoma.payload.device === msg.info.device){
          if ((restautoma.payload.property === undefined)&& (restautoma.payload.value === undefined)) {  // is schema, ok
                  var tuystatus = global.get("tuyastatus");
                  restautoma.payload = JSON.stringify(tuystatus[restautoma.payload.device]);
                  flow.set('restautoma', '_wait');
                  return ([null, restautoma]);
          }
           if ((restautoma.payload.property == msg.info.property) || (restautoma.payload.property == msg.infodp) || (restautoma.payload.property === 'MULTIPLE')) {
             var jdat = JSON.stringify(msg.info);
             restautoma.payload = jdat;
             flow.set('restautoma', '_wait');
             return ([null, restautoma]);
            }
         }
       }
   }

}

// test timeout, if not wait

if (restautoma !== '_wait') {
    var start = flow.get('restt');
    if (((Math.floor(Date.now() / 1000)) - start) > timeout) { // OK exit for timeout
        flow.set('restautoma', '_wait');
        if ((typeof restautoma === 'object') && (restautoma.req !== undefined)) {  
           restautoma.payload = { "status": "request aborted for timeout" };
           return ([null, restautoma]);
           }
    }
}

//noting to do
return ([null, null]);

