// get a device ID array from alldevices.    (alldevices, limit = 'real', 'virtual', 'fake',  null (all))  
function getIdArray(limit = null){
    var arr =[];
    if ((limit === null) || (limit === 'real'))
       context.global.alldevices.real.forEach(dev =>    arr.push(dev.name||dev.cid||dev.id));
    if ((limit === null) || (limit === 'virtual'))
       context.global.alldevices.virtual.forEach(dev => arr.push(dev.name||dev.cid||dev.id));
    if ((limit === null) || (limit === 'fake'))
       context.global.alldevices.fake.forEach(dev =>    arr.push(dev.name||dev.cid||dev.id));
    return arr;
}


