// ------------- main
if (_isGetCommand(msg)) { //  GETs with runtime implementation
    switch (msg.infodp) {
    case "_timerList":
        return ([null, null, null, {topic: "list"}]);
    case "_tuyastatus":
        return ([null, null, null, null, null, null, null, null, null, null, { payload: msg.payload.value}]);
    case "_benchmark":
        newMsg.payload.data.dps[msg.infodp] = flow.get("benchmark");
        return ([null, newMsg]);
    case "_zeroLog":
    case "_zeroTask":               // doing nothing
        newMsg.payload.data.dps[msg.infodp] = "nothing";
        return ([null, newMsg]);
    case "_benchmark_step":               // doing nothing
    case "_benchmark_end":               // doing nothing
          return ([null]);
    }
} // end GET

if (_isSetCommand(msg)) { // a SET
 switch (msg.infodp) {
 // timer
    case "_timerON":
        if (msg.payload.value.id === undefined)
            msg.payload.value["id"] = msg._msgid;
        return ([null, null, null, { topic: "set", payload: msg.payload.value } ]);
    case "_timerOFF":
        newMsg.payload.data.dps[msg.infodp] = "sent";
        return ([null, newMsg, null, { topic: "clear", payload: { id: msg.payload.value } } ]);
// direct access
    case "_doTrigger":
        if(! global.get("modules.core_trigger")){
           newMsg.payload.data.dps[msg.infodp] = "WARNING: this property requires the TRIGGER module.";
           return ([null, newMsg]);
        }
        let triggerDP = global.get("COUNTDP");  // tuya-bridge countdown
        let nt = (typeof msg.payload.value === 'string')?parseInt(msg.payload.value):msg.payload.value ;
        msg = { "payload": {  "dps": triggerDP, "set": nt } };  // this destroy msg.infodp
        newMsg.payload.data.dps["_doTrigger"] = nt.toString();
        return ([msg, newMsg]); // _to tuya_bridge:output 1
    case "_toLowIN":
        newMsg.payload.data.dps[msg.infodp] = msg.payload.value;
        return ([null, newMsg, null, null, msg.payload.value]);
    case "_toFastIN":
        newMsg.payload.data.dps[msg.infodp] = msg.payload.value;
         return ([null, newMsg, null, null, null, { payload: msg.payload.value }]);
    case "_toStdCmd":
        newMsg.payload.data.dps[msg.infodp] = msg.payload.value;
        return ([null, newMsg, null, null, null, null, { payload: msg.payload.value }]);
    case "_toShare":
        newMsg.payload.data.dps[msg.infodp] = msg.payload.value;
        return ([null, newMsg, null, null, null, null, null, msg.payload.value]);
    case "_toLogging":
        newMsg.payload.data.dps[msg.infodp] = "sent";
        return ([null, newMsg, null, null, null, null, null, null, { payload: msg.payload.value } ]);
    case "_toWarn":
       node.warn(msg.payload.value);
       newMsg.payload.data.dps[msg.infodp] = "sent";
       return ([null, newMsg]);
    case "_toGlobal":       
        if((msg.payload.value.value === undefined) || (msg.payload.value.variable === undefined)) {
            node.warn(["ERROR in SET _system.toGlobal: ", msg]);
            return ([null]);     
        }
        const akey = msg.payload.value.variable;
        const avalue = msg.payload.value.value;
        if ((avalue == 'null')|| (avalue == 'NULL')){
                // is GET
               newMsg.payload.data.dps[msg.infodp] = msg.payload.value;
               newMsg.payload.data.dps[msg.infodp].value = global.get(akey);
        } else {
                // is SET
               global.set(akey, avalue);
               newMsg.payload.data.dps[msg.infodp] = msg.payload.value;
        }
        return ([null, newMsg]);     
    case "_toDebug":
       newMsg.payload.data.dps[msg.infodp] = "sent";
       return ([null, newMsg, null, null, null, null, null, null, null, { payload: msg.payload.value } ]);
    case "_beep":
        newMsg.payload.data.dps[msg.infodp] ="done";
        return ([null, newMsg, null, null, null, null, null, null, null, null, null, { payload: flow.get("beep64") }]);
    case "_play" :
        newMsg.payload.data.dps[msg.infodp] = "sent";
        return ([null, newMsg, null, null, null, null, null, null, null, null, null, null, { payload: msg.payload.value }]);

// global.tuyastus
    case "_doSCHEMA":
        // 2.2  returns always a LIST[] 
     // becase is used 'fast_cmds', the controls must be done here
      let o1 = context.global.alldevices.getODev(msg.payload.value);
      let devName = context.global.alldevices.getDevName(o1);
      if ((o1 === undefined)||(o1.dps === undefined)||(o1.dps.count <1) || (o1.capability && o1.capability.includes('NONE'))) {
               newMsg.payload.data.dps[msg.infodp] =  "WARNING: "+ devName + " does not exist or is inaccessible."
               return ([null, newMsg]);
               }
       // uses only multiple GETs
       let list1 = [];
       o1.dps.forEach(function(property) {                               // uses GETs
           let dpname = property.name || property.dp.toString();
           if ((property.capability === undefined) || (["RW","RO","GW","WW"].includes(property.capability)) ) {
                 const gmsg ={  payload:{'device': devName,'property': dpname }} ;  // local GET
                 node.send([null, null, null, null, null, gmsg]);
                 list1.push(dpname);
                 }
          });
         if (list1.length == 0){
                 newMsg.payload.data.dps[msg.infodp] = "WARNING: "+ devName + " without READ DPs.";
                 return ([null, newMsg]);
                 }
      newMsg.payload.data.dps[msg.infodp] = {device: devName, dps_get: list1};
      return ([null, newMsg]);

    case "_doUPDATE":
        let tuyastatus = global.get("tuyastatus");
        let allList    = getIdArray(msg.payload.value);
        let list = [];
        // becase is used 'fast_cmds', the controls must be done here
        if ((allList === undefined)||(allList.count <1)) {
               newMsg.payload.data.dps[msg.infodp] = "WARNING: Not found " +msg.payload.value+" devices";
               return ([null, newMsg]);
               }
        allList.forEach(function(device) {
            let devO = context.global.alldevices.getODev(device);
            var cnx  = context.global.alldevices.getConnectName(devO);
            let devconnected = ((tuyastatus[cnx]) && tuyastatus[cnx]._connected);
            if (devconnected) {
                const gmsg ={  payload:{'device': '_system','property': '_doSCHEMA', 'value': device }} ;
                node.send([null, null, null, null, null,gmsg]);
                list.push(device + '; ok');
                } else {
                    if (tuyastatus[device])
                       list.push(device + '; NOT connected');
                    else
                       list.push(device + '; *** NOT FOUND in tuyastatus');
                }
            });
        newMsg.payload.data.dps[msg.infodp] = {[(msg.payload.value ? msg.payload.value + "_devices":"devices")]:list};
        return ([null, newMsg]);

    case "_tuyastatus":
        return ([null, null, null, null, null, null, null, null, null, null, {
                    payload: msg.payload.value,
                    sysname: msg.payload.device,
                    propname: msg.payload.property } ]);  // to use the usr-names on answer
// benchmark
    case "_zeroTask":        //  a return msg is required to process 'share'
        newMsg.payload.data.dps[msg.infodp] = "done SET " + msg.infodp;
        return ([null, newMsg]);
    case "_zeroLog":         //  analyzes the given 'value' for tests   
       newMsg.payload.data.dps[msg.infodp] = ["Normalized SET value: " + typeof(msg.payload.value), msg.payload.value];
       return ([null, newMsg]); 
    case "_benchmark":
        flow.set("benchmark", msg.payload.value);
        newMsg.payload.data.dps[msg.infodp] = msg.payload.value;
        return ([null, newMsg]);
    case "_doBenchmark":
        let benchmark = flow.get("benchmark");
         //
        flow.set("benchmark_count", 1);
        flow.set("bnch_status", "run")
        //  timer for end benckmark
        var timex = {
            "device": "_system",
            "property": "_timerON",
            "value": {
                "id": "bench",
                "timeout": benchmark.timeout || 10000,
                "alarmPayload":{
                    "share": [{
                            "action": [{
                                    "device": "_system",
                                    "property": "_benchmark_end",
                                    "value":"any"
                            }] }]
                }
            }
        }
        newMsg.payload.data.dps[msg.infodp] = "started";
         return([null, newMsg, null, null, null, {payload:benchmark}, {payload: timex}]);
     case "_benchmark_step":
        let bnch_status = flow.get("bnch_status");
        let c = flow.get("benchmark_count");
// security exit
        if (c > 50000){
              flow.set("bnch_status", "done");
              bnch_status = "done";
         }
        let benchx = flow.get("benchmark");
        switch (bnch_status){
