// local function
// note: the value param can also be a @variable:
//e.g. "@msg.payload.set" => eval(msg.payload.set) 
function _sendMirror(idevice, dpname, value) {
    // builds a fake OUT message
    var newMsg = {
        "payload": {
            "deviceId": idevice,
            "data": {
                "dps": {}
                //          "dps":[dpname = value]
            }
        }
    };
    value = value.trim();
    if (value.startsWith('@')){
        value = eval(value.substring(1));
    }
    newMsg.payload.data.dps[dpname.toString()] = value;
    return newMsg;
}

return _sendMirror(env.get("deviceID"), env.get("fakeDP"), env.get("value"));

const triggerDP = 102;  // tuya-bridge countdown
// normal use: no msg.payload.data.dps[triggerDP]
// ack use:   msg.payload.data.dps[triggerDP] > 800
 if ((msg.payload.data != undefined) && (msg.payload.data.dps != undefined) && (msg.payload.data.dps[triggerDP] < 800))
    return null;
var n = env.get("TRGnumber");
var trig = {
       "payload": {
          "dps":triggerDP, 
          "set": parseInt(n)       
          }
}

return trig ;

