// select messages to fire your EVNTRIGGERXXX.
// the condition can be as complex  as required 
//  works with messages from any device: real, virtual, mirror, system
//

// filter example: 
// Trigger on temperature > 20 °C  (from a virtual device)
 if(msg.payload.deviceId == "bfb073b1b789b5994cro7p")  // from  gateway
    if(msg.payload.data.cid == "00158d00056a1c45")     // device: cid termometer
       if (msg.payload.data.dps[103] >=  200)          // test on undecoded value
         return[{payload:"go"}];                 // in output#1
// filter example: 
// Trigger on PIR on (from mirror device)
 if(msg.payload.deviceId == "733710408caab5e79887")    // the device
       if (msg.payload.data.dps[1010] === true)        // test on value
           return[null, {payload:"go"}];         // in output#2

//  more....
return null;

var delta;

// test on all tuya_bridge messages
// output:  finish/ reset/more loops
if  ((msg.payload.tuyatrigger > 699) &&
     (msg.payload.tuyatrigger < 800)){
     // fallback in case of missed  ACK: restart and keep alive
     flow.set("tsstart", 0);
     return([null, msg, msg]); 
     }

if(msg.payload.tuyatrigger != 0 ) // not ACK,  nothing to do
     return([null,null,null]); 

// standard 0 ACK,  nothing to do
    var old = flow.get("tsstart");
    if (old === 0)  return([null,null,null]);   // not in ping test

    var back = Date.now();
    delta = back - old;
    if ((delta > 2000) || (delta < 20)) { 
// fallback in case of timeout: restart and keep alive
         flow.set("tsstart", 0);
         return([null, null, msg]);
         }

// ok, updates stats
  var stats = flow.get("pingstat") ; 
 //flow.set("pingstat",{
 //     test: 0,
 //     total:0,
 //     min: 100000000000,
 //     max:0
 // set data    
   stats.sum += delta;
    if( stats.min > delta)
         stats.min = delta;
    if( stats.max < delta)
         stats.max = delta;
    stats.test++;  
    stats.total--;   
    flow.set("pingstat", stats);

  if (stats.total >0)  {       // more  tests  
      flow.set("tsstart", 0);
      return [null, null, msg];
      }   

// done, does statistics & msg  
 var st = {
    "deviceId":"_system",
    "data":{
       "dps":{
           "_trgPing":{
               "count": stats.test,
               "avg": Math.floor(stats.sum/stats.test),
               "max": stats.max,
               "min": stats.min
           }
       }
    }};  
   msg.payload = st;
   flow.set("tsstart", 0);
// sends results  
   return([msg, null, null]);

 flow.set("tsstart", 0);
 flow.set("pingstat",{
      total:node.name,
      test: 0,
      sum:0,
      min: 100000000000,
      max:0
});

return msg;

