// selects&executes commands for _system:_trgPing, _trigger
// version 1.1 (use of _callJSONMethod)
// --------------- locals
var alld = global.get("alldevices");
// local functions:
function _callJSONMethod(obj, fname, a, b, c, d) {
    // see: https://stackoverflow.com/questions/49125059/how-to-pass-parameters-to-an-eval-based-function-injavascript
    var wrap = s => "{ return " + obj[fname] + " };" //return the block having function expression
    var func = new Function(wrap(obj[fname]));       // ignore âš 
    return func.call(null).call(obj, a, b, c, d);    //invoke the function using arguments
}

// test: _system GET command? 
function _isGetCommand (idDev, msx) {
 return ((msx.to === idDev) && (msx.payload.property !== undefined) && (msx.payload.value === undefined));
}
// test: _system SET command? 
function _isSetCommand (idDev, msx) {
 return ((msx.to === idDev) && (msx.payload.property !== undefined) && (msx.payload.value !== undefined));
}

// ------------- main
//  partial implementation of _system GET for GET _trgPing SET _trigger

if (_isGetCommand("_system", msg)) {  // a GET
// find object dp
    var oDev = _callJSONMethod(alld, "__getODev", "_system", "fake");
    var oPx  = _callJSONMethod(alld, "__getODps", oDev,  msg.payload.property);
//
  if (oPx.dp != undefined)  // found
    switch (oPx.dp){
          case "_trgPing":
                return([null, msg]);   //to  _trgPing:output 2
       }
    } 

if (_isSetCommand("_system", msg)) {  // a SET
// find object dp
    var GDev = _callJSONMethod(alld, "__getODev", "_system", "fake");
    var GoPx  = _callJSONMethod(alld, "__getODps", GDev,  msg.payload.property);
//
  if (GoPx.dp != undefined)  // found
    switch (GoPx.dp){
       case "_trigger":
           msg = {
             "payload": {
               "dps":7, 
               "set": parseInt(msg.payload.value)       
                }
           };
           return([msg, null]);   // _to tuya_bridge:output 1
       }
    }    

// ignore 
return([null]);
