// local function
// note: the value param can also be a @variable:
//e.g. "@msg.payload.set" => eval(msg.payload.set) 
function _sendMirror(idevice, dpname, value) {
    // builds a fake OUT message
    var newMsg = {
        "payload": {
            "deviceId": idevice,
            "data": {
                "dps": {}
                //          "dps":[dpname = value]
            }
        }
    };
    value = value.toString().trim();
    if (value.startsWith('@')){
        value = eval(value.substring(1));
    }
    newMsg.payload.data.dps[dpname.toString()] = value;
    return newMsg;
}

return _sendMirror(env.get("deviceID"), env.get("fakeDP"), env.get("value"));

// Implementation of GET/SET/SCHEMA/MULTIPLE  commands
// for an 'abstract' device
//    1) verify incoming command
//    2) update the global.tuyadevice (if required)
//    3) one output for every TRIGGER to Tuya cloud
//
// smoke detector accepts only SET, absorb all msg.
// version 1.1 (use of _callJSONMethod)
// --------------- locals
var alld = global.get("alldevices");

// local functions:
function _callJSONMethod(obj, fname, a, b, c, d) {
    // see: https://stackoverflow.com/questions/49125059/how-to-pass-parameters-to-an-eval-based-function-injavascript
    var wrap = s => "{ return " + obj[fname] + " };" //return the block having function expression
        var func = new Function(wrap(obj[fname])); // ignore ⚠
    return func.call(null).call(obj, a, b, c, d); //invoke the function using arguments
}

// test: idDev GET command?
function _isGetCommand(idDev, msx) {
    return ((msx.to === idDev) && (msx.payload.property !== undefined) && (msx.payload.value === undefined));
}

// test: idDev SET command?
function _isSetCommand(idDev, msx) {
    return ((msx.to === idDev) && (msx.payload.property !== undefined) && (msx.payload.value !== undefined));
}

//  set (and create, if required) global.tuyastatus structure from: dev-name, dp-name, value
function _setGlobalValue(devname, dpname, val) {
    if ((devname === undefined) || (dpname === undefined) || (val === undefined))
        return;
    var tuyastatus = global.get("tuyastatus");
    if (tuyastatus == undefined)
        tuyastatus = {};
    if (tuyastatus[devname] == undefined)
        tuyastatus[devname] = {};
    tuyastatus[devname][dpname] = val;
    global.set("tuyastatus", tuyastatus);
}

