            //  language en/it
            if ((msg.payload.value == null) || (msg.payload.value == false) || (msg.payload.value.toUpperCase() === "OFF") || (msg.payload.value.toUpperCase() === "NO"))
                return ([null, msg]); // output#2
            //  else is ON
            return ([msg]); // output#1
        }
