// builds the object
    var res = {
        payload :{
            remote:   flow.get("remote"),
            device:   flow.get("device"),
            property: flow.get("property"),
            value:    flow.get("value")
        }
    }

// processes NULL cases    
    if ((!(res.payload.remote))||(res.payload.remote == 'NULL'))
           delete (res.payload.remote)

     if (res.payload.device == "NULL"){
          delete (res.payload.device);
          delete (res.payload.property);
          delete (res.payload.value);
    }

    if (res.payload.property == "NULL") {
          delete (res.payload.property);
          delete (res.payload.value);
    }

    if (res.payload.value == "NULL")
           delete (res.payload.value);

 return res;

function _upadateParam(_pname){
     var  _xvalue = env.get(_pname);
     if (_xvalue){
      let temp = _xvalue.toString().trim();
        if (temp.startsWith('global.'))
           _xvalue = global.get(temp.substring(7));
        else if(temp.startsWith('flow.'))
           _xvalue = flow.get("$parent."+ temp.substring(5));
        else if(temp.startsWith('msg.'))
           _xvalue = msg[temp.substring(4)];
// extra eval           
        else if(temp.startsWith('@'))
           _xvalue = eval(temp.substring(1));
       }

// optional: to set the parameter dynamically with a message property, same name
      if((msg.command)&&(msg.command[_pname]))
           _xvalue = msg.command[_pname];

// save actual value in the (sub)flow context           
      flow.set(_pname, _xvalue);
      }

// CUSTOMIZE HERE
// get actual values
 _upadateParam("remote");
 _upadateParam("device");
 _upadateParam("property");
 _upadateParam("value");
 // done
return msg;

const FASTDP = "_refreshCycle";   // set: 'off'| time [s]
const REFDP  = "_refresh";        // set: any (trigger)
const DEVID  = env.get("deviceID");
// options for tests
const REFONLY   = {operation: "REFRESH"};
const REFONE    = {operation: "REFRESH", dps:20};
const REFMANY   = {operation: "REFRESH", requestedDPS:[1,9,6,17,18,19,20]};
const REFSCHEMA = {operation: "REFRESH", schema:true};
// in this version used only REFONLY, because the others do not give appreciable differences,
function isNone(value){
    if(value){
        if(typeof(value) === "string"){
            if(value.toUpperCase() === 'OFF') return true;
            if(value.toUpperCase() === 'NO') return true;
            if(value.toUpperCase() === 'NONE') return true;
        }
    return false;
    }
return true;
}

if (DEVID === ""){
 node.warn("ERROR [REFRESH] deviceID not set.");
 return[null];
}
// builds the output message
var newMsg = {
    "payload": {
        "deviceId": DEVID,
        "deviceName": "dummy", // as device node, not used
        "data": {
            "dps": {}
        }
    }
};

// --------------------------------
// ignores pseudoDP
if ((msg.payload === undefined) || (msg.payload.dps === undefined)) return [null, msg];
//
// test for REFDP  SET/GET case
if (msg.payload.dps === REFDP){
    if (msg.payload.set !== undefined) { // is set
        newMsg.payload.data.dps[REFDP] = "sent";
        return ([null, {payload:REFONLY}, newMsg]);
        } else
    return [null];   // no GET
 }

//  test for  REFDP MULTIPLE case
if ((msg.payload.data !== undefined) && (msg.payload.data[REFDP] !== undefined) ) {
    delete msg.payload.data[REFDP];
    newMsg.payload.data.dps[REFDP] = "sent";
    node.send([null, { payload: REFONLY }, newMsg]);
    return ([null, msg]);
}

// ----------------------------------
// test for FASTDP  SET/GET case
if (msg.payload.dps === FASTDP) {
    if (msg.payload.set !== undefined) { // is set
        context.set("fast", msg.payload.set);
        newMsg.payload.data.dps[FASTDP] = msg.payload.set;
        if (isNone(msg.payload.set)) {
             return ([{ payload: "stop" }, null, newMsg]);
        } else{
          return ([{ payload:REFONLY, 
                     loop:{duration: msg.payload.set, units:"Second", maxtimeout: flow.get("Max time"), maxtimeoutunits:"Second"}} , null, newMsg]);
        } 
    } else { // is get, back last value
        newMsg.payload.data.dps[FASTDP] = context.get("fast");
        return ([null, null, newMsg]);
    }
}

// test for FASTDP  MULTIPLE case
if ((msg.payload.data !== undefined) && (msg.payload.data[FASTDP] !== undefined)) {
    let fst = msg.payload.data[FASTDP];
    delete msg.payload.data[FASTDP];
    context.set("fast", fst);
    newMsg.payload.data.dps[FASTDP] = fst;
    if (isNone(fst)) {
             return ([{ payload: "stop" }, null, newMsg]);
        } else{
          return ([{ payload: REFONLY , 
                     loop:{duration: fst, units:"Second", maxtimeout: env.get("Max time"), maxtimeoutunits:"Second"}} , null, newMsg]);
        } 
}

// test for ALL,  SCHEMA case
if (msg.payload.schema) {
    newMsg.payload.data.dps[FASTDP] = context.get("fast");
    return ([null, msg, newMsg]);
}
// else do nothing
return [null, msg];

