func:
// sends any message to DB (sql in msg.topic), updates tuyastatus
// version 1.1 (use of _call_library)
// version 2,0 (use of context.global.alldevices  amd  context.global.datadecode
//-------------------------------------------

// local functions:

// -------------------------------------------- private functions
var oDev = null;
var _dataname = "";

// updates the global "tuyastatus"
// note: for real devices: tuyastatus._connected is handle by static/dynamic retry node.
//       for fake devices: tuyastatus._connected is handle by "xxx flow startup" node in the flow.    
//       for virtual devices:  tuyastatus._connected is handle here
function _addToStatus(remote, dnx, _datanx, dx, tx) {
    var mystatus = global.get("tuyastatus['"+dnx +"']");
    if (mystatus === undefined)
        mystatus = {};
    mystatus[_datanx] = dx;
    if (_datanx != "_connected")
        mystatus._connected = true;
    mystatus._t = tx;
    global.set("tuyastatus['"+dnx+"']", mystatus);
}

// clean data (object, array) using JSON.stringify, to get a string for DB
function formatData(data) {
    var clean = JSON.stringify(data);
    if (clean[0] === '"')
        clean = clean.substring(1).slice(0, -1);
    //    return clean.replace(/'/g, '&#39;');  // sigle quotes are dangerous in sql
    //    return clean.replace(/'/g, '\\x27');
    return clean.replace(/'/g, "\\'");
}

// For DB output, string
function formatName(data) {
    if (data == null)
        return 'NULL';
    //    return data.replace(/'/g, '&#39;');  // sigle quotes are dangerous in sql
    //    return data.replace(/'/g, '\\x27');
    return data.replace(/'/g, "\\'");
}

// true if device is a real device (virtual := cid exists)
function _isreal(mx) {
    return (mx.payload.data.cid === undefined);
}

// returns deviceId (real) or cid (virtual), never fails
function _getID(mx) {
    return ( mx.payload.data.cid || mx.payload.deviceId);
}

// returns data.t or now(), formatted for mySQL, never fails
function _mySQLdatetime(mx) {
    //datetime format for mySQL: '2020-12-18 20:45:02'
    // using 'sv' for date format and local timezone
    if (mx.payload.data.t === undefined)
        return (new Date().toLocaleString('sv'));
    return (new Date(Number(mx.payload.data.t) * 1000).toLocaleString('sv'));
}

// returns val, processed by correct datadecode[] function, if required, and sets  _dataname
function _processData(odev, oPx, mx, adpn, val) {
    _dataname = "";
    if (oPx === undefined) {
        // not found DP? no PSEUDODP? warning and continue (no change value)
        if ( ! context.global.PSEUDODP.includes(adpn.toUpperCase())) // extra pseudo-DPs
            node.warn(["WARNING [OUT data]: In alldevices missed the '" + oDev.name + "':dps[" + adpn + "] definition."]);
        _dataname = adpn;
        return (val);
    }
    _dataname = (oPx.name === undefined) ? adpn : oPx.name;
// general continue
    if ((oPx.typefield === undefined) || (oPx.typefield === 'NONE'))
        return (val); // no process
    if ( context.global.datadecode[oPx.typefield] === undefined) {
        node.warn("ERROR [OUT data]:Not found " + oPx.typefield + " dencode function() for '" + _dataname + "': data unchanged.");
        return (val);
    }
// exception for _zeroLog: no decode return data also if oPx.typefield exists.
    if (oPx.dp === "_zeroLog")
           return(val);
    try {
        return  context.global.datadecode[oPx.typefield](val);
    } catch (error) {
        node.warn(["ERROR [OUT data]:in " + oPx.typefield + " decode function on value (" + (typeof(val) === "string")?val:JSON.stringify(val) + "): " + error,oPx]);
        return (val);
    }

}

// get the device object from alldevices, if it don't exist return undefined + warn
// with a fine handling of error messages.
function _getODevice(mx) {
    var o;
    if (_isreal(mx)) {
        o = context.global.alldevices.getODev(mx.payload.deviceId, "real");
        if (o === undefined) {
            o = context.global.alldevices.getODev( mx.payload.deviceId, "fake");
        }
        if (o === undefined) {
            node.warn("ERROR [OUT data]: ADD to alldevices the real/fake deviceId: " + mx.payload.deviceId + " (" + mx.payload.deviceName + ")");
        }
    } else {
        o = context.global.alldevices.getODev(mx.payload.data.cid, "virtual");
        if (o === undefined) {
            node.warn("ERROR [OUT data]: ADD to alldevices the virtual cid: " + mx.payload.data.cid + " [gateway: " + mx.payload.deviceId + "]");
        }
    }
    return (o);
}

// some ASSERT, just in case...
if (!(msg.payload.deviceId ))  {
   node.warn(["ASSERT fail: in the answer the payload.deviceId must be defined.", msg]) ;   
   return null;
}
if (!(msg.payload.data)) {
    node.warn(["ASSERT fail: in the answer the payload.data is mandatory", msg]);
    return null;
}
// ---------------------  main

var warmsg = null; // info debug (1)
msg.topic = ""; // sql query (2)

var myId = global.get("instance_name");
if (msg.payload.remote !== undefined) myId = msg.payload.remote;

oDev = _getODevice(msg);
if (oDev === undefined)
    return ([null]);    //error sended,  abort
var dname = context.global.alldevices.getDevName(oDev);
msg["from"] =  _getID(msg);
msg["hide"] =  oDev.hide || "";

// now processes dp=value couples
if (msg.payload.data.dps !== undefined) {
    try {
//      node.warn(["out data process IN ", msg]);
     Object.entries(msg.payload.data.dps).forEach(([key, value]) => {
// ---- LOOP for DPs        
        if ((value === undefined) || (value === null)) {
              node.warn(["ERROR [OUT data]: from "+dname+"/dps[" + key + "], the value is not defined.", msg]);
              return ;
              }
           var warmsg = null;
           var oPthis =context.global.alldevices.getODps(oDev, key);
           var pdata = _processData(oDev, oPthis, msg, key, value);
             // builds sql step by step
            msg.topic += " INSERT INTO `messages` (`timestamp`, `daemon`, `action`, `device-id`, `device-name`, `dps`, `dp-name`, `data`, `value`)\n";
            msg.topic += " VALUES ('" + _mySQLdatetime(msg) + "', '" + myId + "' , 'RX', '" + _getID(msg) + "', '" + formatName(dname) +
            "','" + key + "', '" + formatName(_dataname) + "', '" + formatData(value) + "', '" + formatData(pdata) + "') ;\n";
            msg.infodp = key.toString();     // to filter DB
            if (_dataname !== undefined) {
                // timestamp as seconds
                var tx = msg.payload.data.dps.t;
                if (tx === undefined)
                    tx = Math.floor(Date.now() / 1000);
               // info message
               let sstr = "RX: ";
               //  remote_from exists ?
               if (typeof(msg.payload.remote_from) === "string"){
                  if ( msg.payload.remote_from ==="" || msg.payload.remote_from == "NULL")
                           sstr = "RX: ";   // local
 //                        sstr = global.get("instance_name")+": RX: ";
                      else
                  if(global.get("remotemap.local")[msg.payload.remote_from] === undefined)
                         sstr = "UNKNOWN: RX: ";
                      else
                         sstr = msg.payload.remote_from+": RX: ";
                         }
               warmsg = {
                   payload: [sstr + dname + "/" + _dataname, pdata]
                   };

             //from 2.2.0
                warmsg['hide'] = msg.hide + (((oPthis) && (oPthis.hide))? oPthis.hide:"");
                if (!~warmsg.hide.indexOf("K"))         //  K kills also tuyastatus update
                    _addToStatus(msg.payload.remote_from, dname, _dataname, pdata, tx);
                 //more data for fast post processing
                if(msg.payload.remote_from !== undefined)
                     warmsg['remote_from'] = msg.payload.remote_from;
                warmsg['from'] = oDev.cid || oDev.id;
                warmsg['infodp'] = key.toString();         // extra  for filter...
                warmsg['info'] = {};            //  required by REST check and by share
                warmsg['info']['device'] = dname;
                warmsg['info']['property'] = _dataname;
                warmsg['info']['value'] = pdata;           // decoded
                let thishare =  (oPthis === undefined? undefined: oPthis.share ); // auto actions
                // share only local
                if ((thishare !== undefined) && (msg.payload.remote_from === undefined))
                     warmsg['share'] = thishare;
//             node.warn(["out outproc ", warmsg,  msg]);
               node.send([warmsg, null]); // SQL are appended
            }

    });
    } catch (error){
         node.warn(["Error in " + dname + " device message: " + error, msg]);
        return ([null]);

    }

} else {
    if (typeof(msg.payload.data) === "string" ){
        if (msg.payload.data == "json obj data unvalid") {
            node.warn(["ERROR [OUT data]: from device '" + dname + "': not JSON data but 'json obj data unvalid': operation not allowed?.", msg]);
        } else {
            //  usually bad tuya id o key
            var buff = Buffer.from(msg.payload.data);
            node.warn(["ERROR [OUT data]: from device '" + dname + "': not JSON data but HEX:" + buff.toString('hex') + "  ('" + msg.payload.data + "')", msg]);
        } }
    else {
       node.warn(["ERROR [OUT data]: from device '" + dname + "', maybe bad format ? ", msg]);
    }
}

if (msg.topic !== "") // some processed, SQL exists
    return ([null, msg]);

// true if device is a real device (virtual == cid exists)
function _isreal(odev) {
    return (odev.cid === undefined);
}

// returns deviceId (real) or cid (virtual), never fails
function _getID(odev) {
    return (odev.cid === undefined ? odev.id : odev.cid);
}

// true if is a GET command  (GET == msg.payload.value not exists)
function _isget(mx) {
    return (mx.payload.value === undefined);
}

// true if is a SET command  (SET == msg.value  exists)
function _isset(mx) {
    return ((mx.payload.property) && (mx.payload.value !== undefined));
}


// returns now(), formatted for mySQL, never fails
function _mySQLdatetime() {
    //datetime format for mySQL: '2020-12-18 20:45:02'
    // using 'sv' for date format (but local timezone)
    return (new Date().toLocaleString('sv'));
}

// For DB output, object/string
function formatData(data) {
    if (data == null)
        return 'NULL';
    var clean = JSON.stringify(data);
    if (clean[0] === '"')
        clean = clean.substring(1).slice(0, -1);
    //    return clean.replace(/'/g, '&#39;');  // sigle quotes are dangerous in sql
    //    return clean.replace(/'/g, '\\x27');
    return clean.replace(/'/g, "\\'");
}

// For DB output, string
function formatName(data) {
    if (data == null)
        return 'NULL';
    //    return data.replace(/'/g, '&#39;');  // sigle quotes are dangerous in sql
    //    return data.replace(/'/g, '\\x27');
    return data.toString().replace(/'/g, "\\'");
}

function _getHome() {
    return global.get("instance_name");
}

// ==============================  main function

var req = {};
var warn = null;
var query = {};
//
//---------------------- some assert. just in case...

if ((!msg.payload)) {
    node.warn(["ASSERT fail: msg.payload must exist.", msg]);
    return [null];
}

if ((msg.payload.property !== undefined) && (typeof(msg.payload.property) !== "string")) {
    node.warn(["ASSERT fail: msg.payload.property, if it exists, must be a string.", msg]);
    return [null];
}

// no remote, local
if (msg.payload.remote && (msg.payload.remote === _getHome())) {
    delete msg.payload.remote;
}

// extra, local list, using REST ??
if ((!msg.payload.remote) && (!msg.payload.device) && (!msg.payload.property) && (!msg.payload.value)){
     msg.payload.remote = _getHome();
 }


if ((!msg.payload.remote) && (!msg.payload.device)) {
    node.warn(["ASSERT fail: msg.payload.device must exist in local.", msg]);
    return [null];
}

if ((msg.payload.remote) &&  (!global.get('modules.core_system')))  {
    node.warn(["ASSERT fail: 'remote' requires the 'system' module installed.", msg]);
    return [null];
}
// ---------------- expand messages for remote

if (msg.payload.remote) {
    let dx = msg.payload;
    msg.payload = {};
