// this node processes "_connected" event:
//   - filters duplicated msgs
//   - updates the global.tuyastatus
//   - sends message to logging. 
//  note: logging processes for dp '_connected' is special: 
//    the 'dp' '_connected' it is not required in alldevices.
//  You can add to some devices the dp='_connected' 
//    for special processing, e.g. 'share'.


tuyastatus = global.get("tuyastatus");
if (tuyastatus === undefined) return null;

// local functions:
var CONN = "_connected";
// access key
var oDev =    context.global.alldevices.getODev(msg.payload.deviceId, "real");
if (oDev === undefined) return null;
var dname =   context.global.alldevices.getDevName(oDev);
var usrCon =  context.global.alldevices.getDpsName(oDev, CONN);
// testing:
if( tuyastatus[dname] === undefined) tuyastatus[dname] = {};
var xdev = tuyastatus[dname];
var oldconnected = xdev[usrCon];
var nowconnected = msg.payload.data.dps._connected;
if (nowconnected !== oldconnected) {
        xdev[usrCon] = nowconnected;
        global.set("tuyastatus", tuyastatus);
        return (msg);
    }
return (null);

// kills unwanted tuyaDEAMON EVENTS messages & MQTT

// some ASSERT, just in case...
if((typeof(msg.to) !== "string") && (typeof(msg.from) !== "string") )
         node.warn(["ASSERT fail: in msg the to/from (string) must exist.", msg]) ;   
if(typeof(msg.infodp) !== "string")
         node.warn(["ASSERT fail: in msg the msg.infodp (string) must exist." , msg]) ;   
// from 2.2.0
if(typeof(msg.hide) !== "string")
         node.warn(["ASSERT fail: in msg the msg.hide (string) must exist." , msg]) ;   

// ==================== KILL EVENT debug + MQTT:  SYSTEM defined (can be updated)
switch (msg.from){    
   case "_system":
        if (msg.infodp === "_proxy")     return(null);   //  TRG internal use: REMOTE commands 
        if (msg.infodp === "_toLogging") return(null);   //  internal use: REST remote echo
        if (msg.infodp === "_zeroTask")  return(null);   //  by definition: fast benchmarks
        break;
}

// =================== KILL EVENY debug + MQTT: user defined in alldevices
if ((!!~msg.hide.indexOf("E")) || (!!~msg.hide.indexOf("K")))
            return null; // killed

return msg;

// CUSTOMIZATION

// to filter and route data to 0..n DB, for logging (TX) or execute sql commands
// Default using 2 DB:  'local' and 'remote'.
// 3 outputs: local, no log (for TX and RX events)/ locale + log(for sqlDBlocal)/remote + log(for sqlDBremote)

// Some assert, just in case.....
if(typeof(msg.to) !== "string" )
         node.warn(["ASSERT fail: in msg the msg.to (string) must exist.", msg]) ;   
if(typeof(msg.infodp) !== "string")
