         msg.payload["remote_from"] = myId; 
// stringify         
         let jdat2 = JSON.stringify(msg.payload);
         msg.payload = jdat2;
         return ([msg, null]);
  }

 //  node.warn(["Trace general remote",msg]);

// general case:  
// -------- is GET, read data from tuyastatus
if (msg.payload.value === undefined) {
  msg.payload["remote_from"] = myId; 
  if (tuyastatus[msg.payload.device][msg.payload.property] !== undefined)  { 
         let connected = tuyastatus[msg.payload.device]["_connected"];
         if(connected)
             msg.payload['value'] = tuyastatus[msg.payload.device][msg.payload.property];
          else
             msg.payload['value'] ="disconnected";
         let jdat2 = JSON.stringify(msg.payload);
         msg.payload = jdat2;
         return ([msg, null]);
         } else {
         msg.payload['value'] = "none";
         let jdat2 = JSON.stringify(msg.payload);
