function recursiveExecute(obj) {
    for (const property in obj) {
 //       node.warn(["testing: "+property,obj[property]]);
        if ((typeof(obj[property]) === 'string') && (obj[property].startsWith('@') )) {
 //           node.warn(["processing: ", obj[property]]);
            obj[property] = eval(obj[property].substring(1));
        } else if (typeof(obj[property]) === 'object') {
            recursiveExecute(obj[property]);
        }
    }
}    

function evalExecute(aVal){
    // accepts: strings, JSON, object
    // output: string or object after eval leaf like '@....'
    if ((typeof(aVal) === 'string') && (aVal.startsWith('{') || aVal.startsWith('['))){
        data = JSON.parse(aVal);
        recursiveExecute(data);
        return data;
    } 
    if ((typeof(aVal) === 'string') && aVal.startsWith('@')){
        return eval(aVal.substring(1));
    }
    if (typeof(aVal) === 'object'){
        recursiveExecute(aVal);
        return(aVal);
    }
    return(aVal);
}




