        function hmTomin(hm) { // local function
            pieces = hm.split(':');
            return (parseInt(pieces[0]) * 60 + parseInt(pieces[1]));
        }
        function dayscode(val) {
            result = 0;
            mask = 1;
            for (var i = 0; i < 7; i++) {
                result |= (val.charAt(i) == '-') ? 0 : mask;
                mask = mask << 1;
            }
            return (result);
        }
        var o = value;
        if (typeof value === 'string')
            o = JSON.parse(value);
        var buf = Buffer.alloc(6 * o.length);
        for (let i = 0; i < o.length; i++) {
            buf.writeUInt8((o[i].active) ? 1 : 0, i * 6 + 0);
            buf.writeUInt8(dayscode(o[i].days), i * 6 + 1);
            buf.writeUInt16BE(hmTomin(o[i].start), i * 6 + 2);
            buf.writeUInt16BE(hmTomin(o[i].end), i * 6 + 4);
        }
        return buf.toString('base64');
    };

