// constructs 2 global objects (singleton):
context.global.PSEUDODP   = global.get("PSEUDODP");
context.global.alldevices = global.get("alldevices");

// adding required methods to context.global.alldevices.

// return the device object
//  called with user-name|(deviceID|CID)
//  limit: null (= all, default)|"real"|"virtual"!"fake"
//  returns: object|undefined (= not found)
context.global.alldevices.getODev = function(id, limit = null){
      function _checkobj(list, dx) {
          var  o = list.find(device => device.name === dx);
          if (o === undefined)
              o = list.find(device => device.id == dx);
          if (o === undefined)
              o = list.find(device => device.cid == dx);
          return (o);
          }  
      function _okcheck(lim, branch) {
          return ((lim === null) || (lim == branch))
          }
// finds ODev       
   var odev;
   if (id === undefined) return (odev);
   if ((odev == undefined) && _okcheck(limit, 'real'))
      odev = _checkobj(this.real, id);
   if ((odev == undefined) && _okcheck(limit, 'virtual'))
      odev = _checkobj(this.virtual, id);
   if ((odev == undefined) && _okcheck(limit, 'fake'))
      odev = _checkobj(this.fake, id);
   return (odev);
}// given device-object, returns device-name or undefinef
context.global.alldevices.getDevName =  function(odev) { 
    if (odev === undefined) return undefined; 
    return (odev.name === undefined ? (odev.cid == undefined ? odev.id : odev.cid) : odev.name);
    }        
//   given device-object, returns connection-name (device or gateway) or undefined 
context.global.alldevices.getConnectName = function(odev) {
    if (odev === undefined) return undefined;  
    if (odev.cid === undefined) 
         return(odev.name === undefined ? odev.id : odev.name);  
    var o_gat = this.real.find(device => device.id === odev.gateway);   
    if (o_gat === undefined) return o_gat;   
    return (o_gat.name === undefined ? o_gat.id : o_gat.name);
    }

// given device-object and a property (name|dp) returns dp-object
context.global.alldevices.getODps = function(odev, property) { 
    if (odev === undefined) return undefined;
    if (Array.isArray(odev.dps)) {  
          var p = odev.dps.find(data => ((data.name === property) || (data.dp == property))); 
          return (p);
          }    
    return (undefined);
    }

// given device-object and a property (name|dp) returns dp-name
 context.global.alldevices.getDpsName = function(odev, property) {
 if (Array.isArray(odev.dps)) {
     var p = odev.dps.find(data => ((data.name === property) || (data.dp == property))); 
     if(p !== undefined)
          return (p.name === undefined ? p.dp : p.name);
      }  
    return (property);
    }
//  given a msg-std: device + (property + (value)), device-object, DP-object 
//  returns a msg normalized for internal use:
//  redefined to use with 'tuyastatus' and info
//  `msg.payload.device   = usr-dev-name|msg.to`
//  `msg.payload.property = undef|usr-dp-name|msg.infodp`
//  `msg.payload.value    = undef|any`  
//  plus, used as keys:
//    `msg.to     = cid|deviceId`         (mandatory)
//    `msg.infodp = dp|pseudoDP`          (mandatory)
//    `msg.hide   = ""|"(K)(C)(E)(R)(T)"` (visibility, since 2.2.0)
//  plus, in case of error:
//     msg.error  ="ERROR message"        (abort)
//     msg.warning="WARNING message"      (continue)
// use:
/*
    var o = context.global.alldevices.getODev(msg.payload.device);
    var p = context.global.alldevices.getODps(o, msg.payload.property);
    msg   = context.global.alldevices.normalize( msg, o, p);
    if (msg.error){
        node.warn(["[from-node] "+ msg.error, msg]);
        return([null]);
    }
    if (msg.warning){
       node.warn("[from-node] "+ msg.warning);
    }
*/
 context.global.alldevices.normalize = function (xmsg, Do, Po){
    if(!Do) {
         xmsg['error'] = "ERROR [normalize]: not found the device '"+xmsg.payload.device+"' in global.alldevices."; 
         return (xmsg); 
         }
    xmsg.payload.device = (Do.name) ? Do.name: (Do.cid || Do.id);
    xmsg['to'] =  Do.cid || Do.id;
    if(! this.fake.includes(Do)) 
        xmsg['toDev'] = Do.cid ? Do.gateway: Do.id; 
    xmsg['infodp'] = 'schema';
    if (xmsg.payload.property) { 
        if ((Po === undefined) || (Po.dp === undefined)) { 
            xmsg.infodp  = xmsg.payload.property; 
            if (! context.global.PSEUDODP.includes(xmsg.payload.property.toUpperCase())) 
               xmsg['warning'] ="WARNING [normalize]: In alldevices missed the '"+xmsg.payload.device+"':dps[" + xmsg.payload.property + "] definition."; 
        } else { 
            xmsg.infodp =  Po.dp.toString(); 
            xmsg.payload.property =  Po.name || Po.dp; 
        }
    } 
    if (xmsg['infodp'] === 'schema')
       delete(xmsg.payload.value);
    xmsg.hide = ((Do && Do.hide)? Do.hide:"") + ((Po && Po.hide)? Po.hide:"");
    return (xmsg); 
 }

// given a value and a DP-object
// returns the value encoded or the good type (uses: typefield | type | default rules) )
 context.global.alldevices.encodeValue =  function(value, dpObj={}){
      if((value===undefined)||(dpObj===undefined)) return(value);
      // general values 'null' 
      if((value==='')||(value===null)||(value==='NULL')||(value==='null'))return(null);
      if((!dpObj.typefield)||(dpObj.typefield==='NONE')||(dpObj.typefield==='NULL')){
          try{
              switch(dpObj.type){      // type forces output
                 case'boolean':
                     return(!((value===false)||(value==='false')||(value==='FALSE')||(value===0)));
                 case'int':
                 case'enum':
                     return(Number(value));
                 case'string':
                     return(value.toString());
                 default:              // also undefined: default rules
                    if(typeof(value)==='boolean') return(value);
                    if(value==='false') return(false);
                    if(value==='true') return(true);
                    return((parseInt(value) == value)? parseInt(value):value);
                                  // "37" => 37, but "4.5" => "4.5"
                  }
          }
          catch(error){
              return({error:'ERROR [encodeValue]: In conversion from value('+value+') to type '+dpObj.type+': '+error});
             }
      }else{    // try the encode function
         if(!context.global.dataencode[dpObj.typefield])
              return({error:'ERROR [encodeValue]: Not found the encode.'+dpObj.typefield+' function().'});
         let nval;
         try{
             nval=(context.global.dataencode[dpObj.typefield](value));
             }
         catch(error){
             return({error:'ERROR [encodeValue]: In encode.'+dpObj.typefield+' function, on value ('+value+'): '+error});
             }
         return(nval);
      }
 }  

